public void run()
{
    super();

    if (!closeNow)
    {
        this.setFormTitleCaption();
        this.initActionPane();

        element.updateDesign();

        //refreshes are now allowed
        formInitialized    = true;

        this.parmRegistrationMode(registrationMode);

        this.refreshContent();
    }
}

public void runMenuItem(str _menuItemName, MenuItemType _menuItemType, Common _record)
{
    MenuFunction    mf;
    Args            args;

    args = new Args();
    args.record(_record);
    args.caller(this);
    mf = new MenuFunction(_menuItemName, _menuItemType);
    mf.run(args);
}

public void updateRegistrationModeButtons()
{
    RegistrationModeSelectButton.toggleValue(0);
    RegistrationModeStartButton.toggleValue(0);
    RegistrationModeCompleteButton.toggleValue(0);
    RegistrationModeEmptyButton.toggleValue(0);

    switch (this.parmRegistrationMode())
    {
        case KanbanScanMode::StartTransfer:
            RegistrationModeStartButton.toggleValue(1);
            break;
        case KanbanScanMode::EndTransfer:
            RegistrationModeCompleteButton.toggleValue(1);
            break;
        case KanbanScanMode::Empty:
            RegistrationModeEmptyButton.toggleValue(1);
            break;
        case KanbanScanMode::SelectKanban:
            RegistrationModeSelectButton.toggleValue(1);
            break;
    }
}

