public void setFormTitleCaption()
{
    str formTitleFilterCaption;

    if (kanbanBoardTmpFilterCriteria.InventLocationIdFrom)
    {
        formTitleFilterCaption += strFmt("@SYS313421",kanbanBoardTmpFilterCriteria.InventLocationIdFrom) + ' ';
    }
    if (kanbanBoardTmpFilterCriteria.wmsLocationIdFrom)
    {
        formTitleFilterCaption += strFmt("@SYS313422",kanbanBoardTmpFilterCriteria.wmsLocationIdFrom) + ' ';
    }
    if (kanbanBoardTmpFilterCriteria.InventLocationIdTo)
    {
        formTitleFilterCaption += strFmt("@SYS313424",kanbanBoardTmpFilterCriteria.InventLocationIdTo) + ' ';
    }
    if (kanbanBoardTmpFilterCriteria.wmsLocationIdTo)
    {
        formTitleFilterCaption += strFmt("@SYS313422",kanbanBoardTmpFilterCriteria.wmsLocationIdTo) + ' ';
    }
    if (kanbanBoardTmpFilterCriteria.PlanReference)
    {
        formTitleFilterCaption += strFmt("@SYS313425",kanbanBoardTmpFilterCriteria.planReferenceName()) + ' ';
    }
    if (kanbanBoardTmpFilterCriteria.PlanActivity)
    {
        formTitleFilterCaption += strFmt("@SYS313426",kanbanBoardTmpFilterCriteria.planActvityName()) + ' ';
    }

    if (formTitleFilterCaption)
    {
        this.design().caption(strFmt("@SYS313427","@SYS313428",formTitleFilterCaption));
    }
    else
    {
        this.design().caption("@SYS313428");
    }
}

