public void startRefreshCycleTimer()
{
    // If the user wants automatic refresh, start the new timer
    if (refreshCycle > 0)
    {
        if (!timerControl)
        {
            timerControl = this.design().addControlEx(classStr(TimerControl), 'KanbanBoardTransferJobTimerControl');
            timerControl.onElapsed += eventhandler(this.timerControlElapsed);
        }

        timerControl.interval(refreshCycle);
        timerControl.enabled(true);
    }
    else if (timerControl)
    {
        timerControl.enabled(false);
    }
}

/// <summary>
/// Event raised when the timer control elapses.
/// </summary>
public void timerControlElapsed()
{
    this.refreshContent();
}

/// <summary>
/// Overrides various form related actions initiated by the user.
/// </summary>
/// <param name="_taskId">
/// The ID that represents the action taken by the user.
/// </param>
/// <returns>
/// An integer representing the return status of the action.
/// </returns>
public int task(int _taskId)
{
    #Task

    int ret;

    ret = super(_taskId);

    if (_taskId == #taskF5)
    {
        this.setButtonAccess();
    }

    if (_taskId == #taskRefresh)
    {
        element.resetGrid();
        element.refreshContent(true);
    }

    return ret;
}

public boolean unpack(container _packedClass)
{
    int     version = RunBase::getVersion(_packedClass);
    boolean result  = true;

    switch (version)
    {
        case #CurrentVersion:
            [version,#CurrentList] = _packedClass;
            break;
        default:
            result = false;
    }

    return result;
}

