public void executeQuery()
{
    Query                       query;
    QueryBuildDataSource        qbdsKanbanJobTmpPickList;
    QueryBuildDataSource        qbdsInventDim;
    container                   selectedJobs;

    if (PickingListTab.isActivePage())
    {
        if (!element.parmTestMode())
        {
            delete_from kanbanJobTmpPickList;

            //fill the table data with the picking list of the selected jobs
            selectedJobs = KanbanBoardForm::selectedRecords(kanbanBoardTmpTransferJob_ds);
            kanbanJobTmpPickList.initTransferJobBoard(selectedJobs);

            //create the query to link to Inventory dimensions
            query = new Query();

            qbdsKanbanJobTmpPickList = query.addDataSource(tableNum(KanbanJobTmpPickList));
            qbdsKanbanJobTmpPickList.name(kanbanJobTmpPickList_ds.name());

            qbdsInventDim = qbdsKanbanJobTmpPickList.addDataSource(tableNum(InventDim));
            qbdsInventDim.name(inventDimPickListPegging_ds.name());
            qbdsInventDim.addLink(fieldNum(KanbanJobTmpPickList,InventDimId),fieldNum(InventDim,InventDimId));
            qbdsInventDim.joinMode(JoinMode::InnerJoin);

            kanbanJobTmpPickList_ds.query(query);

            //datasource is shared between pegging and picking list, it must be rejoined in every query execution
            inventDimPickListPegging_ds.joinSource(kanbanJobTmpPickList_ds.name());
            inventDimPickListPegging_ds.executeQuery();
        }

        super();
    }
}

public void executeQuery()
{
    if (   PickingListTab.isActivePage()
        || PeggingTab.isActivePage())
    {
        super();
    }
}

public void executeQuery()
{
    Query                   query;
    QueryBuildDataSource    queryBuildDataSource;
    QueryBuildDataSource    queryBuildDataSourceInventDim;

    if (   !element.parmTestMode()
        && !refreshKanbanJobTmpPegging)
    {
        delete_from kanbanJobTmpPegging;

        kanbanJobTmpPegging.init(kanbanBoardTransferJobList.selectedJobs());

        //create the query to link to Inventory dimensions
        query = new Query();

        queryBuildDataSource = query.addDataSource(tableNum(KanbanJobTmpPegging));
        queryBuildDataSource.name(kanbanJobTmpPegging_ds.name());

        queryBuildDataSourceInventDim = queryBuildDataSource.addDataSource(tableNum(InventDim));
        queryBuildDataSourceInventDim.name(inventDimPickListPegging_ds.name());
        queryBuildDataSourceInventDim.addLink(fieldNum(KanbanJobTmpPegging,InventDimId),fieldNum(InventDim,InventDimId));
        queryBuildDataSourceInventDim.joinMode(JoinMode::InnerJoin);

        kanbanJobTmpPegging_ds.query(query);

        //this datasource is shared between pegging and picking list, it must be rejoined in every query execution
        inventDimPickListPegging_ds.joinSource(kanbanJobTmpPegging_ds.name());
        inventDimPickListPegging_ds.executeQuery();
    }

    super();
}

public void executeQuery()
{
    QueryBuildDataSource    queryBuildDataSource;
    Query                   query;

    //add the sort: most recent messages first
    query                = this.query();
    queryBuildDataSource = query.dataSourceTable(tableNum(KanbanBoardTmpMessageBoard));
    queryBuildDataSource.addOrderByField(fieldNum(KanbanBoardTmpMessageBoard,CreatedDateTime),SortOrder::Descending);

    super();
}

