public void init()
{
    QueryBuildDataSource        qbdsKanbanBoardTmpTransferJob;

    super();

    qbdsKanbanBoardTmpTransferJob = this.query().dataSourceTable(tableNum(KanbanBoardTmpTransferJob));
    qbdsKanbanBoardTmpTransferJob.addSortField(fieldNum(KanbanBoardTmpTransferJob,Status),SortOrder::Descending);
    qbdsKanbanBoardTmpTransferJob.addSortField(fieldNum(KanbanBoardTmpTransferJob,ActualEndDateTime),SortOrder::Ascending);
    qbdsKanbanBoardTmpTransferJob.addSortField(fieldNum(KanbanBoardTmpTransferJob,DueDateTime),SortOrder::Ascending);

    resetSelection = true;
}

