public void executeQuery()
{
    RefRecId                    focusedJob   = kanbanBoardTmpTransferJob.Job;
    Set                         selectedJobs = new Set(Types::Int64);
    KanbanBoardTmpTransferJob   currentJob;

    if (!resetSelection)
    {
        currentJob = kanbanBoardTmpTransferJob_ds.getFirst(1) ? kanbanBoardTmpTransferJob_ds.getFirst(1) : kanbanBoardTmpTransferJob_ds.cursor();
        while (currentJob)
        {
            selectedJobs.add(currentJob.Job);
            currentJob = kanbanBoardTmpTransferJob_ds.getNext();
        }
    }

    if (!element.parmTestMode())
    {
        delete_from kanbanBoardTmpTransferJob;

        //fill the table data
        kanbanBoardTmpTransferJob.initTransferJobBoard(kanbanBoardTmpFilterCriteria,
                                                        visibleNotPlannedJobs,
                                                        visibleInProgressJobs,
                                                        visibleCompletedJobs,
                                                        registrationMode == KanbanScanMode::SelectKanban
                                                            && kanbanBoardTmpFilterCriteria.KanbanCardId != '');
        super();
    }

    if (resetSelection)
    {
        this.findValue(fieldNum(KanbanBoardTmpTransferJob,Status),SysQuery::valueNot(LeanKanbanJobStatus::Completed));
        resetSelection = false;
    }
    else
    {
        for (currentJob = kanbanBoardTmpTransferJob_ds.getFirst();currentJob;currentJob = kanbanBoardTmpTransferJob_ds.getNext())
        {
            if (selectedJobs.in(currentJob.Job))
            {
                kanbanBoardTmpTransferJob_ds.markRecord(currentJob,1);
            }
        }

        element.selectTransferJob(focusedJob);
    }
}

