{
    super();
    kanbanBoardTransferJobForm.runMenuItem(menuitemActionStr(KanbanTransferJobInProgressSilent),MenuItemType::Action,kanbanBoardTmpTransferJob);
    element.smartRefreshContent();
}

public void clicked()
{
    super();
    kanbanBoardTransferJobForm.runMenuItem(menuitemActionStr(KanbanTransferJobCompleteSilent),MenuItemType::Action,kanbanBoardTmpTransferJob);

    if (kanbanBoardTmpTransferJob.kanbanJob().Status == LeanKanbanJobStatus::Completed)
    {
        resetSelection = true;
    }

    element.smartRefreshContent();
}

public boolean modified()
{
    boolean ret = super();

    KanbanCardId kanbanCardId = this.text();
    if (ret && kanbanCardId)
    {
        element.resetMessageBoard();
        this.text('');

        if (kanbanBoardTransferJobForm.checkCardId(kanbanCardId,kanbanBoardTmpMessageBoard)
            && kanbanBoardTransferJobList.checkRegistrationMode(kanbanCardId,element.parmRegistrationMode(),element.messageBoard()))
        {
            // make sure we pull the data from the database for the selection scenario
            if (registrationMode == KanbanScanMode::SelectKanban)
            {
                element.resetGrid();
                kanbanBoardTmpFilterCriteria.KanbanCardId = kanbanCardId;

                // refresh all content, including the pegging tab, as this one is used
                // for error messaging
                element.refreshContent(true);
                element.refreshKanbanJobTmpPegging(true, true);
            }

            // try to process the card
            if (kanbanBoardTransferJobForm.processCard(kanbanCardId, element.parmRegistrationMode(), element.messageBoard()))
            {
                element.refreshContent();
            }
        }
        else
        {
            element.refreshMessageBoard();
        }
    }

    return ret;
}

public void clicked()
{
    element.parmRegistrationMode(KanbanScanMode::StartTransfer);
    element.updateRegistrationModeButtons();
    super();
}

public void clicked()
