private boolean assignCirculatingCard(KanbanRefRecId _kanbanRefRecId)
{
    FormRun         assignCardFormRun;
    Object          obj;
    boolean         cardAssigned = false;

    // Open the form to assign kanbans to cards
    assignCardFormRun = classfactory.formRunClass(new Args(formStr(KanbanAssignCard)));
    assignCardFormRun.init();
    obj = assignCardFormRun;
    obj.parmKanbanRefRecId(_kanbanRefRecId);
    assignCardFormRun.run();
    assignCardFormRun.wait();

    if (assignCardFormRun.closedOk())
    {
        cardAssigned = true;
    }

    return cardAssigned;
}

public boolean assignCirculatingCards()
{
    KanbanBoardTmpProcessJob        processJobSelected;

    KanbanJob       kanbanJob;
    Kanban          kanban;
    KanbanRule      kanbanRule;

    boolean allCardsAssigned = true;

    refreshAllowed = false;

    // Get the first multi selection or single selection record
    processJobSelected = kanbanBoardTmpProcessJob_ds.getFirst(1) ?
                         kanbanBoardTmpProcessJob_ds.getFirst(1) :
                         kanbanBoardTmpProcessJob_ds.cursor();

    while (processJobSelected && allCardsAssigned)
    {
        // Check if this kanban requires a manual card assignment
        if (processJobSelected.CardId == '')
        {
            kanbanJob = KanbanJob::find(processJobSelected.Job, false);
            kanban = Kanban::find (kanbanJob.Kanban,false);
            kanbanRule = KanbanRule::find(kanban.KanbanRule);

            if (kanbanRule.UsesCirculatingCards == NoYes::Yes
                && kanbanRule.CardAssignmentType == KanbanCardAssignmentType::Manual)
            {
                allCardsAssigned = this.assignCirculatingCard(kanbanJob.Kanban);
            }
        }

        processJobSelected = kanbanBoardTmpProcessJob_ds.getNext();
    }

    refreshAllowed = true;

    return allCardsAssigned;
}

public void close()
