public void init()
{
    // Values are needed by the time datasources are initialized (as part of the call to super)
    xSysLastValue::getLast(element);

    if (element.args() &&
        element.args().dataset() == tablenum(KanbanBoardTmpFilterCriteria))
    {
        KanbanBoardTmpFilterCriteria kanbanBoardTmpFilterCriteria = element.args().record();

        workCellFilterRecId = kanbanBoardTmpFilterCriteria.WorkCell;
        workCellFilter      = WrkCtrResourceGroup::find(workCellFilterRecId).WrkCtrId;
    }

    isSuppressKanbanQty = NoYes::Yes;

    super();

    if (element.args())
    {
        testMode = element.args().parmEnum() == NoYes::Yes;
    }

    // Pick a work cell when the form opens
    if (!testMode)
    {
        this.initWorkcell();
    }

    // Initialize the form manager
    kanbanBoardWorkCellForm = KanbanBoardWorkCellForm::newForm(this, inventDimProcessJob_ds, inventDimPickListPegging_ds, inventDimTransferJob_ds);

    // Initialize the form controls
    if (!testMode)
    {
        this.initDesign();
    }

    // Update the overall design (load data, etc)
    kanbanBoardWorkCellForm.updateDesign(InventDimFormDesignUpdate::Init);

    // Allow refresh.
    refreshAllowed = true;
}

private void initActionPane()
{
    // Setup button managers
    formButtonManager        = FormButtonManager::construct();
    kanbanBoardButtonManager = KanbanBoardWorkCellButtonManager::newDefault(kanbanBoardProcessJobList, kanbanBoardTransferJobList);

    formButtonManager.parmRuleImplementer(kanbanBoardButtonManager);

    formButtonManager.setDatasources(kanbanBoardTmpProcessJob, kanbanBoardTmpTransferJob);

    // Register the buttons/groups of the Picking tab
    formButtonManager.registerButtonRule(pickingProduction, prepare, methodStr(KanbanBoardWorkCellButtonManager, checkPrepareEnabled));
    formButtonManager.registerButtonRule(pickingProduction, pickingCreateEvent, methodStr(KanbanBoardWorkCellButtonManager, checkCreateEventKanbansEnabled));
    formButtonManager.registerButtonRule(pickingTransfer, startTransfer, methodStr(KanbanBoardWorkCellButtonManager, checkStartTransferEnabled));
    formButtonManager.registerButtonRule(pickingTransfer, endTransfer, methodStr(KanbanBoardWorkCellButtonManager, checkCompleteTransferEnabled));
    formButtonManager.registerButtonRuleOnGroup(pickingPrint, methodStr(KanbanBoardWorkCellButtonManager, checkPrintEnabled));
    formButtonManager.registerButtonRule(maintainStatusButtonGroup,revertStatusButton,methodStr(KanbanBoardWorkCellButtonManager,checkRevertStatus));
    formButtonManager.registerButtonRule(maintainStatusButtonGroup,cancelButton,methodStr(KanbanBoardWorkCellButtonManager,checkCancelMaintain));
    formButtonManager.registerButtonRule(maintainStatusButtonGroup,prioritizeButton,methodStr(KanbanBoardWorkCellButtonManager,checkPrioritizeMaintain));

    // Register the buttons/groups of the Manufacturing tab
    formButtonManager.registerButtonRule(manufacturingProduction, start, methodStr(KanbanBoardWorkCellButtonManager, checkStartEnabled));
    formButtonManager.registerButtonRule(manufacturingProduction, end, methodStr(KanbanBoardWorkCellButtonManager, checkCompleteEnabled));
    formButtonManager.registerButtonRule(manufacturingProduction, endDetails, methodStr(KanbanBoardWorkCellButtonManager, checkCompleteDetailsEnabled));
    formButtonManager.registerButtonRuleOnGroup(manufacturingPrint, methodStr(KanbanBoardWorkCellButtonManager, checkPrintEnabled));

