public void updateRegistrationModeButtons()
{
    PickingPrepareScanMode.toggleValue(0);
    PickingEmptyScanMode.toggleValue(0);
    PickingStartTransferScanMode.toggleValue(0);
    PickingEndTransferScanMode.toggleValue(0);

    ManufacturingStartScanMode.toggleValue(0);
    ManufacturingEndScanMode.toggleValue(0);
    ManufacturingSelectKanbanScanMode.toggleValue(0);

    switch (this.parmRegistrationMode())
    {
        case KanbanScanMode::Prepare:
            PickingPrepareScanMode.toggleValue(1);
            break;
        case KanbanScanMode::Empty:
            PickingEmptyScanMode.toggleValue(1);
            break;
        case KanbanScanMode::StartTransfer:
            PickingStartTransferScanMode.toggleValue(1);
            break;
        case KanbanScanMode::EndTransfer:
            PickingEndTransferScanMode.toggleValue(1);
            break;
        case KanbanScanMode::StartProcess:
            ManufacturingStartScanMode.toggleValue(1);
            break;
        case KanbanScanMode::EndProcess:
            ManufacturingEndScanMode.toggleValue(1);
            break;
        case KanbanScanMode::SelectKanban:
            ManufacturingSelectKanbanScanMode.toggleValue(1);
            break;
        case KanbanScanMode::StartEndProcess:
            ManufacturingStartScanMode.toggleValue(1);
            ManufacturingEndScanMode.toggleValue(1);
            break;
    }
}

