private void updateWorkCellFilter(WrkCtrId _workCellFilter)
{
    WrkCtrResourceGroup wrkCtrResourceGroup = WrkCtrResourceGroup::findWrkCtrId(_workCellFilter);

    workCellFilterRecId = wrkCtrResourceGroup.RecId;
    workCellFilter      = wrkCtrResourceGroup.WrkCtrId;

    refreshJob = true;
    this.setFormTitleCaption();
    this.refreshContent();
    this.refreshCycleTimeBar();
}

public void processScannedCard(KanbanCardId _cardId)
{
    // Ensure that the card exists and is assigned to a kanban
    if (kanbanBoardWorkCellForm.checkCardId(_cardId, kanbanBoardTmpMessageBoard))
    {
        switch (this.parmRegistrationMode())
        {
            case KanbanScanMode::SelectKanban:
                kanbanBoardWorkCellForm.selectCard(_cardId, tableStr(KanbanBoardTmpProcessJob), kanbanBoardTmpMessageBoard);
                break;

            case KanbanScanMode::StartProcess:
                if (kanbanBoardWorkCellForm.selectCard(_cardId, tableStr(KanbanBoardTmpProcessJob), kanbanBoardTmpMessageBoard))
                {
                    this.runMenuItemAction(menuitemActionStr(KanbanJobInProgressSilent), kanbanBoardTmpProcessJob);
                }
                break;

            case KanbanScanMode::EndProcess:
                if (kanbanBoardWorkCellForm.selectCard(_cardId, tableStr(KanbanBoardTmpProcessJob), kanbanBoardTmpMessageBoard))
                {
                    this.runMenuItemAction(menuitemActionStr(KanbanJobCompleteSilent), kanbanBoardTmpProcessJob);
                }
                break;

            case KanbanScanMode::StartEndProcess:
                if (kanbanBoardWorkCellForm.selectCard(_cardId, tableStr(KanbanBoardTmpProcessJob), kanbanBoardTmpMessageBoard))
                {
                    if (kanbanBoardTmpProcessJob.Status == LeanKanbanJobStatus::InProgress)
                    {
                        this.runMenuItemAction(menuitemActionStr(KanbanJobCompleteSilent), kanbanBoardTmpProcessJob);
                    }
                    else
                    {
                        this.runMenuItemAction(menuitemActionStr(KanbanJobInProgressSilent), kanbanBoardTmpProcessJob);
                    }
                }
                break;

            case KanbanScanMode::Prepare:
                if (kanbanBoardWorkCellForm.selectCard(_cardId, tableStr(KanbanBoardTmpProcessJob), kanbanBoardTmpMessageBoard))
                {
                    this.runMenuItemAction(menuitemActionStr(KanbanJobPrepareSilent), kanbanBoardTmpProcessJob);
                }
                break;

            case KanbanScanMode::Empty:
                Kanban kanban = Kanban::findKanbanCardId(_cardId);
                if (kanban)
                {
                    this.runMenuItemAction(menuitemActionStr(KanbanEmptySilent), kanban);
                }
                break;

            case KanbanScanMode::StartTransfer:
                if (kanbanBoardWorkCellForm.selectCard(_cardId, tableStr(KanbanBoardTmpTransferJob), kanbanBoardTmpMessageBoard))
                {
                    this.runMenuItemAction(menuitemActionStr(KanbanJobInProgressSilent), kanbanBoardTmpTransferJob);
                }
                break;

            case KanbanScanMode::EndTransfer:
                if (kanbanBoardWorkCellForm.selectCard(_cardId, tableStr(KanbanBoardTmpTransferJob), kanbanBoardTmpMessageBoard))
                {
                    this.runMenuItemAction(menuitemActionStr(KanbanJobCompleteSilent), kanbanBoardTmpTransferJob);
                }
                break;
        }
    }

    this.refreshContent();
}

public void refreshContent()
{
    if (refreshAllowed)
    {
        this.refreshKanbanJobList(false);
        this.refreshTransferJobList(false);
        this.refreshMessageBoard();

        this.setButtonAccess();
    }

    this.startRefreshCycleTimer();
}

public void refreshCycleTimeBar()
{
    boolean                 enabled;
    LeanAverageTakt         averageTakt;
    LeanProdFlowActualTakt  actualTakt;
    LeanMinimumTakt         minTakt;
    LeanMaximumTakt         maxTakt;

    KanbanBoardCycleTimeMeter   kanbanBoardCycleTimeMeter;
    LeanTaktStatus              leanTaktStatus;

    [enabled,averageTakt,actualTakt,minTakt,maxTakt] = kanbanBoardCycleTimeMeter::taktDataFromWrkCtrResourceGroupServer(workCellFilterRecId);

    if (enabled)
    {
        kanbanBoardCycleTimeMeter = KanbanBoardCycleTimeMeter::newFromWrkCtrResourceGroup(workCellFilterRecId);

        leanTaktStatus = kanbanBoardCycleTimeMeter.leanTaktStatus();
        Takt_Status.text(enum2str(leanTaktStatus));

        LeanTmpProductionFlowIndicator.setTmpData(
            LeanTmpProductionFlowIndicator::populateFromTaktValues(averageTakt, minTakt, maxTakt, actualTakt));
    }
    else
    {
        delete_from LeanTmpProductionFlowIndicator;

        Takt_Status.text('');
    }

    LeanTmpProductionFlowIndicator_DS.research();
}

public void refreshKanbanJobDetails()
{
    if (prodInstructions.isActivePage())
    {
        this.loadProductionInstructions();
