private void setScannerAccess()
{
    boolean scannerAllowed = workCellFilterRecId != 0;

    PickingScanMode.enabled(scannerAllowed);
    ManufacturingScanMode.enabled(scannerAllowed);
    ScanCardId.enabled(scannerAllowed);

    if (!scannerAllowed)
    {
        ScannerTab.collapse();
    }
}

private void setKanbanQuantityOverviewAccess()
{
    btnKanbanQuantityOverviewFinishedGoods.enabled(workCellFilterRecId);
    btnKanbanQuantityOverviewMaterials.enabled(workCellFilterRecId);
}

private void setForceRefreshTransferJobs()
{
    // The transfer job list will not refresh if not visible, but is required for scanning
    // so if the scanning mode is start or end transfer, the refresh/initialization of the
    // list will be enforced
    switch (this.parmRegistrationMode())
    {
        case KanbanScanMode::StartTransfer:
        case KanbanScanMode::EndTransfer:
            forceRefreshTransferJobs = true;
            break;

        default:
            forceRefreshTransferJobs = false;
    }
}

