public void startRefreshCycleTimer()
{
    // If the user wants automatic refresh, start the new timer
    if (refreshCycle > 0)
    {
        if (!timerControl)
        {
            timerControl = this.design().addControlEx(classStr(TimerControl), 'KanbanBoardWorkCellTimerControl');
            timerControl.onElapsed += eventhandler(this.timerControlElapsed);
        }

        timerControl.interval(refreshCycle);
        timerControl.enabled(true);
    }
    else if (timerControl)
    {
        timerControl.enabled(false);
    }
}

/// <summary>
/// Event raised when the timer control elapses.
/// </summary>
public void timerControlElapsed()
{
    this.refreshContent();
}

public boolean unpack(container packedClass)
{
    int version     = RunBase::getVersion(packedClass);

    switch (version)
    {
        case 1:
            packedClass += DefaultShowPickingListForEntireKanban;
        // fall through
        case CurrentVersion:
            [version,#CurrentList] = packedClass;
            return true;
    }

    return false;
}

