public void executeQuery()
{
    Query                       query;
    QueryBuildDataSource        qbS, qbsDim;
    Set                         selectedJobs = new Set(Types::Int64);
    RecId                       focusedJob   = kanbanBoardTmpProcessJob.Job;
    KanbanBoardTmpProcessJob    currentJob;

    if (refreshAllowed || element.parmTestMode() == true)
    {
        if (!refreshJob)
        {
            currentJob = kanbanBoardTmpProcessJob_ds.getFirst(1) ? kanbanBoardTmpProcessJob_ds.getFirst(1) : kanbanBoardTmpProcessJob_ds.cursor();
            while (currentJob)
            {
                selectedJobs.add(currentJob.Job);
                currentJob = kanbanBoardTmpProcessJob_ds.getNext();
            }
        }

        if (element.parmTestMode() == false)
        {
            delete_from kanbanBoardTmpProcessJob;

            // Fill the table data
            kanbanBoardTmpProcessJob.init(element.parmWorkCellFilterRecId(),
                                          element.parmVisibleCompletedJobs(),
                                          element.parmVisiblePlannedJobs(),
                                          element.parmVisiblePreparedJobs(),
                                          element.parmVisibleInProgressJobs());

            // Create the query to link to Inventory dimensions
            // The drawback of this operation is that the grid now is able to sort columns
            query = new Query();

            qbS   = query.addDataSource(tableNum(KanbanBoardTmpProcessJob));
            qbS.name(kanbanBoardTmpProcessJob_ds.name());

            qbsDim = qbS.addDataSource(tableNum(InventDim));
            qbsDim.name(inventDimProcessJob_ds.name());
            qbsDim.addLink(fieldNum(KanbanBoardTmpProcessJob,InventDimId),fieldNum(InventDim,InventDimId));
            qbsDim.joinMode(JoinMode::InnerJoin);

            qbS.addSortField(fieldNum(KanbanBoardTmpProcessJob, Status), SortOrder::Descending);
            qbS.addSortField(fieldNum(KanbanBoardTmpProcessJob, ActualEndDateTime), SortOrder::Ascending);
            qbS.addSortField(fieldNum(KanbanBoardTmpProcessJob, PlannedPeriod), SortOrder::Ascending);
            qbS.addSortField(fieldNum(KanbanBoardTmpProcessJob, Sequence), SortOrder::Ascending);

            kanbanBoardTmpProcessJob_ds.query(query);
        }

        super();

        // This removes the inventory dimensions previous values from the details pane when no kanban jobs are returned.
        inventDimProcessJob_ds.refresh();

        if (refreshJob)
        {
            kanbanBoardTmpProcessJob_ds.findValue(fieldNum(KanbanBoardTmpProcessJob, Status), SysQuery::valueNot(LeanKanbanJobStatus::Completed));
            refreshJob = false;
        }
        else
        {
            for (currentJob = kanbanBoardTmpProcessJob_ds.getFirst() ; currentJob ; currentJob = kanbanBoardTmpProcessJob_ds.getNext())
            {
                if (selectedJobs.in(currentJob.Job))
                {
                    kanbanBoardTmpProcessJob_ds.markRecord(currentJob, 1);
                }
            }

            element.selectProcessJob(focusedJob);
        }
    }
}

public display boolean overdueDisplay(KanbanBoardTmpProcessJob _kanbanBoardTmpProcessJob)
{
    return DateTimeUtil::getSystemDateTime() > _kanbanBoardTmpProcessJob.DueDateTime;
}

private QueryFilter findQueryFilter(Query _query, QueryBuildDataSource _queryBuildDataSource, str _fieldName)
{
    for (int i = 1; i <= _query.queryFilterCount(); i++)
    {
        QueryFilter qf = _query.queryFilter(i);

        if (qf.dataSource().name() == _queryBuildDataSource.name() && qf.field() == _fieldName)
        {
            return qf;
        }
    }

    return null;
}

private str formatFilterStr(str _filterStr)
{
    if (_filterStr == '\"\"')
    {
        _filterStr = '';
    }

    return _filterStr;
}

