    Kanban::jumpRefKanban(Kanban::find(kanbanBoardTmpProcessJob.Kanban), element);
}

public void executeQuery()
{
    Query                       query;
    QueryBuildDataSource        qbS,qbsDim;

    if (pickList.isActivePage())
    {
        if (element.parmTestMode() == false)
        {
            delete_from kanbanJobTmpPickList;

            // Fill the table data with the picking list of the selected jobs
            kanbanJobTmpPickList.initProcessJobBoard(
                                    kanbanBoardProcessJobList.selectedJobs()
                                    ,
                                    showPickingListForEntireKanban
                                    );

            // Create the query to link to Inventory dimensions
            query = new Query();

            qbS   = query.addDataSource(tableNum(KanbanJobTmpPickList));
            qbS.name(kanbanJobTmpPickList_ds.name());

            qbsDim = qbS.addDataSource(tableNum(InventDim));
            qbsDim.name(inventDimPickListPegging_ds.name());
            qbsDim.addLink(fieldNum(KanbanJobTmpPickList,InventDimId),fieldNum(InventDim,InventDimId));
            qbsDim.joinMode(JoinMode::InnerJoin);

            kanbanJobTmpPickList_ds.query(query);

            // This datasource is shared between pegging and picking list, it must be rejoined in every query execution
            inventDimPickListPegging_ds.joinSource(kanbanJobTmpPickList_ds.name());
            inventDimPickListPegging_ds.executeQuery();
        }

        super();
    }
}

public void executeQuery()
{
    if (   pickList.isActivePage()
        || kanbanPegging.isActivePage())
    {
        super();
    }
}

public void executeQuery()
{
    Query                   query;
    QueryBuildDataSource    qbS,qbsDim;

    if (kanbanPegging.isActivePage())
    {
        if (   element.parmTestMode() == false
            && !refreshKanbanJobTmpPegging)
        {
            delete_from kanbanJobTmpPegging;

            kanbanJobTmpPegging.init(
                                     kanbanBoardProcessJobList.selectedJobs()
                                     ,
                                     showPickingListForEntireKanban
                                     );

            //create the query to link to Inventory dimensions
            query = new Query();
            qbS   = query.addDataSource(tableNum(KanbanJobTmpPegging));
            qbS.name(kanbanJobTmpPegging_ds.name());

            qbsDim = qbS.addDataSource(tableNum(InventDim));
            qbsDim.name(inventDimPickListPegging_ds.name());
            qbsDim.addLink(fieldNum(KanbanJobTmpPegging,InventDimId),fieldNum(InventDim,InventDimId));
            qbsDim.joinMode(JoinMode::InnerJoin);

            kanbanJobTmpPegging_ds.query(query);

            //this datasource is shared between pegging and picking list, it must be rejoined in every query execution
            inventDimPickListPegging_ds.joinSource(kanbanJobTmpPegging_ds.name());
            inventDimPickListPegging_ds.executeQuery();
        }

        super();
    }
}

public void executeQuery()
{
    if (refreshAllowed)
    {
        // Add the sort: most recent messages first
        var qbS = this.query().dataSourceTable(tableNum(KanbanBoardTmpMessageBoard));
        qbS.addOrderByField(fieldNum(KanbanBoardTmpMessageBoard, CreatedDateTime), SortOrder::Descending);
        // CreatedDateTime logs time only up to a second. Sorting by the RecId field ensures messages are shown in the right order even if they where created in a very small window of time.
        qbS.addOrderByField(fieldNum(KanbanBoardTmpMessageBoard, RecId), SortOrder::Descending); 

        super();
    }
}

