public void executeQuery()
{
    Query                   query;
    QueryBuildDataSource    qbS,qbsDim;

    if (   refreshAllowed
        && (   forceRefreshTransferJobs
            || kanbanTransferJobs.isExpanded()))
    {
        forceRefreshTransferJobs = false;

        if (element.parmTestMode() == false)
        {
            delete_from kanbanBoardTmpTransferJob;

            // Fill the table data
            kanbanBoardTmpTransferJob.initProcessJobBoard(element.parmWorkCellFilterRecId());

            // Create the query to link to Inventory dimensions
            query = new Query();

            qbS   = query.addDataSource(tableNum(KanbanBoardTmpTransferJob));
            qbS.name(kanbanBoardTmpTransferJob_ds.name());

            qbsDim = qbS.addDataSource(tableNum(InventDim));
            qbsDim.name(inventDimTransferJob_ds.name());
            qbsDim.addLink(fieldNum(KanbanBoardTmpTransferJob,InventDimId),fieldNum(InventDim,InventDimId));
            qbsDim.joinMode(JoinMode::InnerJoin);

            kanbanBoardTmpTransferJob_ds.query(query);
        }
        super();
    }
}

