public void dialogClosed(xFormRun _formRun)
{
    Object  object;

    super(_formRun);

    if (_formRun.closedOk())
    {
        object = _formRun;
        refreshCycle = object.parmRefreshCycle();
    }

    element.startRefreshCycleTimer();
}

public void dialogClosed(xFormRun _formRun)
{
    Object  object;

    super(_formRun);

    if (_formRun.closedOk())
    {
        object = _formRun;
        visiblePlannedJobs      = object.parmPlannedJobs();
        visibleCompletedJobs    = object.parmCompletedJobs();
        visiblePreparedJobs     = object.parmPreparedJobs();
        visibleInProgressJobs   = object.parmInProgressJobs();
        showPickingListForEntireKanban = object.parmShowEntirePickingList();
    }

    element.refreshKanbanJobList();
}

