public boolean modified()
{
    boolean ret;

    ret = super();

    KanbanCardId  cardId = scanCardId.text();

    // If the card is empty, leave the field doing nothing
    if (cardId != '')
    {
        scanCardId.text('');

        // Process the specified card according to the scan mode
        element.processScannedCard(cardId);
    }

    return ret;
}

