public boolean modified()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        if (this.text())
        {
            ret = KanbanBoardWorkCellForm.isWorkCellFilterValid(this.text());
        }

        if (ret)
        {
            element.updateWorkCellFilter(this.text());
        }
    }

    return ret;
}

