public void clicked()
{
    KanbanJobPickingList kanbanJobPickingListLocal;

    select firstonly kanbanJobPickingListPool
            where kanbanJobPickingListPool.WavedQty > 0
            exists join kanbanJobPickingListLocal
                where kanbanJobPickingListLocal.InventTransId == kanbanJobPickingListPool.InventTransId
                   && kanbanJobPickingListLocal.ItemId == KanbanJobTmpPickList.ItemId
                   && kanbanJobPickingListLocal.InventDimId == KanbanJobTmpPickList.InventDimId
                   && kanbanJobPickingListLocal.Job == KanbanBoardTmpProcessJob.Job;

    element.runMenuItemAction(menuitemActionStr(WHSPoolReducePickedQuantityKanban), kanbanJobPickingListPool);
}

