[Form]
public class KanbanJobSchedulingListPage extends FormRun
{
    const int none = 3;//Need to keep this as a numeric constant and not add to the KanbanListPageState enum, as we will use the enum as a state selector on the form, and do not want to show none as a state.
    const int endDateBucket = 14;

    KanbanListPageState         currentState = KanbanListPageState::All;
    int                         refreshCycle = 0;
    boolean                     differentStates = false;

    ParmId                      parmId;
    ReqTrans                    currentReqTrans;
    SysLookupMultiSelectCtrl    workCellMsCtrl;
    date                        today;
    date                        minPlannedDate, maxPlannedDate;
    str                         workCellFilterStr;
    date                        plannedStartDate, plannedEndDate;
    container                   jobsToMoveForward;
    boolean                     moveForwardEnabled = true;
    boolean                     moveBackwardEnabled = true;
    str                         workCellMultiSelectCtrlString;
    str                         workCellFilterUserValue;
    str                         workCellFilterCurrentValue;
    ItemId                      lastSelectedItemId;
    Map                         postponedMoveMap;

    InventDimCtrl_Frm_EditDimensions        inventDimFormSetup;

    private const int CurrentVersion = 0;

    #localmacro.CurrentList
        workCellFilterStr,
        plannedStartDate,
        plannedEndDate,
        currentState,
        refreshCycle
    #endmacro

