private str formatMultiselectRange(str controlValue)
{
    return strReplace(controlValue, ';', ',');
}

private date getPlannedKanbanMinDate(str workCellIdRange)
{
    Query q = new Query();
    QueryBuildDataSource qbds = q.addDataSource(tablenum(KanbanJobSchedule));
    qbds.addSelectionField(fieldNum(KanbanJobSchedule, PlannedPeriod), SelectionField::Min);

    qbds = qbds.addDataSource(tableNum(KanbanJob));
    qbds.relations(true);
    qbds.addRange(fieldNum(KanbanJob, Status)).value(queryValue(LeanKanbanJobStatus::Planned));

    qbds = qbds.addDataSource(tableNum(WrkCtrResourceGroup));
    qbds.relations(true);
    qbds.addRange(fieldNum(WrkCtrResourceGroup, WrkCtrId)).value(this.formatMultiselectRange(workCellIdRange));

    QueryRun qr = new QueryRun(q);

    qr.next();

    KanbanJobSchedule schedule = qr.get(tablenum(KanbanJobSchedule));

    return schedule.PlannedPeriod;
}

public void init()
{
    today = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone());

    xSysLastValue::getLast(this);

    if (element.args().record() is WrkCtrResourceGroup)
    {
        WrkCtrResourceGroup resourceGroup = element.args().record();
        workCellFilterStr = resourceGroup.WrkCtrId;
    }

    workCellFilterCurrentValue = workCellFilterStr;
    minPlannedDate = this.getPlannedKanbanMinDate(workCellFilterStr);
    maxPlannedDate = today + endDateBucket;

    if (plannedStartDate < minPlannedDate)
    {
        // Only periods with incompleted kanbans should be shown by default
        plannedStartDate = minPlannedDate;
    }
    if (plannedEndDate > maxPlannedDate || plannedEndDate == dateNull())
    {
        plannedEndDate = maxPlannedDate;
    }

    super();

    // Initializing control values from syslast value if necessary.
    PeriodStart.dateValue(plannedStartDate);
    PeriodEnd.dateValue(plannedEndDate);

    StatusDropDown.selection(currentState);

    workCellMsCtrl = SysLookupMultiSelectCtrl::construct(element, WorkCellDropdown, querystr(KanbanJobWorkCell));

    workCellMsCtrl.set([conNull(), str2con(workCellFilterStr, ';')]);

    WorkCellDropdown.text(workCellFilterStr);

    currentReqTrans = KanbanJobScheduleOperation::getReqTransRecord(KanbanJob);

    this.setFormState(currentState, KanbanJob.Status);

    this.updateDesign(InventDimFormDesignUpdate::Init);

    this.refreshData();
}

public void initParmDefault()
{
    workCellFilterStr = '';
    plannedStartDate = minPlannedDate;
    plannedEndDate = maxPlannedDate;
    currentState = KanbanListPageState::All;
    refreshCycle = 0;
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    InventDimParm inventDimParmVisible;

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_EditDimensions::newFromForm(element);
            }

            inventDimFormSetup.parmSkipOnHandLookUp(true);

            inventDimParmVisible.setAllProductDimensions();

            inventDimFormSetup.parmDimParmVisibleGrid(inventDimParmVisible);
        // fall through below.

        case InventDimFormDesignUpdate::Active:
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemId(Kanban.ItemId));
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange:
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemId(Kanban.ItemId));
            InventDim.clearNotSelectedDim(inventDimFormSetup.parmDimParmEnabled());
            inventDimFormSetup.formSetControls(true);
            break;

        default:
            throw error(strFmt ("@SYS54195", funcName()));
    }
}

public InventDimCtrl_Frm_EditDimensions inventDimSetupObject()
{
    return inventDimFormSetup;
}

private void addToPostponeMoveMap(KanbanJob _kanbanJobToProcess)
{
    var kanbanJobScheduleToProcess = KanbanJobSchedule::findJob(_kanbanJobToProcess.RecId);
    if (!postponedMoveMap.exists(kanbanJobScheduleToProcess.PlannedPeriod))
    {
        postponedMoveMap.insert(kanbanJobScheduleToProcess.PlannedPeriod, [buf2Con(_kanbanJobToProcess), buf2Con(kanbanJobScheduleToProcess)]);
    }
}

/// <summary>
/// Utility method made to centrally process the kanban jobs selected to enable multi selection and not to duplicate the code.
/// </summary>
/// <param name = "_processType">The type of the process to execute.</param>
public void processKanbanJob(KanbanListPageOperationType _processType)
{
    KanbanJob           kanbanJobToProcess;
    KanbanJob           kanbanJobFocus;
    boolean             firstJob = true;
    LeanKanbanJobStatus currentJobStatus;
    boolean             refreshNeeded = true;

    moveForwardEnabled = true;
    moveBackwardEnabled = true;

    MultiSelectionHelper multiSelectHelper = MultiSelectionHelper::construct();
    multiSelectHelper.parmDatasource(KanbanJob_DS);

    postponedMoveMap = new Map(Types::Date, Types::Container);
    kanbanJobToProcess = multiSelectHelper.getFirst();
    while (kanbanJobToProcess != null)
    {
        switch (_processType)
        {
            case KanbanListPageOperationType::MoveForward:
            case KanbanListPageOperationType::MoveStartNext:
            case KanbanListPageOperationType::MoveStartPrev:
                this.addToPostponeMoveMap(kanbanJobToProcess);
                refreshNeeded = false;
                jobsToMoveForward += kanbanJobToProcess;
                break;
            case KanbanListPageOperationType::MoveBackward:
                this.addToPostponeMoveMap(kanbanJobToProcess);
                KanbanJobScheduleOperation::moveBackward(kanbanJobToProcess);
                refreshNeeded = false;
                break;
            case KanbanListPageOperationType::MoveEndNext:
                this.addToPostponeMoveMap(kanbanJobToProcess);
                KanbanJobScheduleOperation::moveEndNextPeriod(kanbanJobToProcess);
                refreshNeeded = false;
                break;
            case KanbanListPageOperationType::MoveEndPrev:
                this.addToPostponeMoveMap(kanbanJobToProcess);
                KanbanJobScheduleOperation::moveEndPreviousPeriod(kanbanJobToProcess);
                refreshNeeded = false;
                break;
            case KanbanListPageOperationType::PlanUnplanned:
                KanbanJobAutoPlan::newKanbanJob(kanbanJobToProcess, true).run();
                break;
            case KanbanListPageOperationType::CreateEventKanbans:
                KanbanJobScheduleOperation::createEventKanbans(kanbanJobToProcess);
                break;
            case KanbanListPageOperationType::PlanKanbanPeggingTree:
                KanbanJobScheduleOperation::planPeggingTree(kanbanJobToProcess);
                break;
            case KanbanListPageOperationType::CheckPeggingTreeAvailable:
                KanbanJobScheduleOperation::checkPeggingTreeAvailable(kanbanJobToProcess);
                break;
            case KanbanListPageOperationType::ChangeProdStatus:
                parmId = KanbanJobScheduleOperation::changeStatus(kanbanJobToProcess, parmId, firstJob);
                firstJob = false;
                break;
            case KanbanListPageOperationType::MarkChanged:
                refreshNeeded = false;
                differentStates = false;
                if (firstJob)
                {
                    moveForwardEnabled = KanbanJobScheduleOperation::canMoveForward(kanbanJobToProcess);
                    moveBackwardEnabled = KanbanJobScheduleOperation::canMoveBackward(kanbanJobToProcess);
                    currentJobStatus = kanbanJobToProcess.Status;
                    firstJob = false;
                }
                else if (currentJobStatus != kanbanJobToProcess.Status
                    && (currentJobStatus == LeanKanbanJobStatus::NotPlanned || kanbanJobToProcess.Status == LeanKanbanJobStatus::NotPlanned))
                {
                    differentStates = true;
                    this.setFormState(none, kanbanJobToProcess.Status);
                    KanbanJob_DS.positionToRecord(kanbanJobToProcess);
                    return; //To force the loop to not execute the rest of the query which is unnecessary.
                }
                else
                {
                    moveForwardEnabled = moveForwardEnabled != KanbanJobScheduleOperation::canMoveForward(kanbanJobToProcess) ? false : moveForwardEnabled;
                    moveBackwardEnabled = moveBackwardEnabled != KanbanJobScheduleOperation::canMovebackward(kanbanJobToProcess) ? false : moveBackwardEnabled;
                }
                this.setFormState(currentState, kanbanJobToProcess.Status);
                break;
        }
        kanbanJobFocus = KanbanJob::find(kanbanJobToProcess.RecId);
        kanbanJobToProcess = multiSelectHelper.getNext();
    }

    if (refreshNeeded)
    {
        this.refreshData();
        KanbanJob_DS.positionToRecord(kanbanJobFocus);
    }

    Forward.enabled(moveForwardEnabled);
    Backward.enabled(moveBackwardEnabled);
}

/// <summary>
/// Due to possible multiselect order issues (i.e. we need to move the first record the last while moving forward or start). We need to traverse the records in the opposite order.
/// </summary>
/// <param name = "_processType">The type of the process to execute.</param>
public void moveJobsForwardOrStart(KanbanListPageOperationType _processType)
{
    int size = conLen(jobsToMoveForward);

    for (int i = size; i > 0; i--)
    {
        switch (_processType)
        {
            case KanbanListPageOperationType::MoveForward:
                KanbanJobScheduleOperation::moveForward(conPeek(jobsToMoveForward, i));
                break;
            case KanbanListPageOperationType::MoveStartNext:
                KanbanJobScheduleOperation::moveStartNextPeriod(conPeek(jobsToMoveForward, i));
                break;
            case KanbanListPageOperationType::MoveStartPrev:
                KanbanJobScheduleOperation::moveStartPreviousPeriod(conPeek(jobsToMoveForward, i));
                break;
        }
    }

    jobsToMoveForward = connull();
}

