public void setDateRange(date _fromDate, date _toDate)
{
    QueryBuildDataSource qbds = KanbanJobSchedule_DS.queryRunQueryBuildDataSource();

    if (qbds)
    {
        QueryFilter queryFilter = SysQuery::findOrCreateQueryFilter(KanbanJobSchedule_DS.queryRun().query(), qbds, fieldstr(KanbanJobSchedule, PlannedPeriod));

        if (queryFilter)
        {
            queryFilter.value(this.getDateRangeFilterQuery(_fromDate, _toDate, qbds));

            this.refreshData();
        }
    }
}

/// <summary>
/// Sets the date range filter.
/// </summary>
/// <param name = "_fromDate">The starting period date.</param>
/// <param name = "_toDate">The end period date.</param>
/// <param name = "_qbds">The query builder data source.</param>
/// <returns></returns>
public str getDateRangeFilterQuery(date _fromDate, date _toDate, QueryBuildDataSource _qbds)
{
    str queryString = strFmt('(((%1.%2 >= %3) && (%1.%2 <= %4))|| (%5.%6 == %7))',
                        _qbds.name(),
                        fieldstr(KanbanJobSchedule, PlannedPeriod),
                        date2StrXpp(_fromDate),
                        date2StrXpp(_toDate),
                        KanbanJob_DS.name(),
                        fieldstr(KanbanJob, Status),
                        0);// LeanKanbanJobStatus::NotPlanned

    return queryString;
}

/// <summary>
/// Sets the work cell filter.
/// </summary>
public void setWorkcellFilter()
{
    str                     tmpFilterValue;
    container               workCells = workCellMsCtrl.getSelectedFieldValues();
    QueryBuildDataSource    qbds = WrkCtrResourceGroup_DS.queryRunQueryBuildDataSource();

    if (qbds)
    {
        QueryFilter queryFilter = SysQuery::findOrCreateQueryFilter(WrkCtrResourceGroup_DS.queryRun().query(), qbds, fieldstr(WrkCtrResourceGroup, WrkCtrId));

        if (queryFilter)
        {
            if (conPeek(workCells, 1) != '')
            {
                str workCellsContToString = con2Str(workCells, ';');
                if (workCellMultiSelectCtrlString != workCellsContToString)
                {
                    workCellMultiSelectCtrlString = workCellsContToString;
                    tmpFilterValue = workCellMultiSelectCtrlString;
                }
                else
                {
                    tmpFilterValue = workCellFilterUserValue;
                }
            }
            else
            {
                tmpFilterValue = workCellFilterUserValue;
            }

            if (workCellFilterCurrentValue != tmpFilterValue)
            {
                workCellFilterCurrentValue = tmpFilterValue;

                var selectedValues = str2con(tmpFilterValue, ';');
                workCellMsCtrl.set([conNull(), selectedValues]);
                WorkCellDropdown.text(workCellFilterCurrentValue);
                queryFilter.value(element.formatMultiselectRange(workCellFilterCurrentValue));
                element.refreshData();
            }
        }
    }
}

