/// <summary>
/// Sets the form state to control the button statuses, executable actions etc.
/// </summary>
/// <param name = "_formState">The current state of the form.</param>
/// <param name = "_selectedJobStatus">Status of the selected job, from kanban job status.</param>
public str setFormState(int _formState, LeanKanbanJobStatus _selectedJobStatus)
{
    str statusQueryStr = SysQuery::valueUnlimited();

    switch (_formState)
    {
        case(KanbanListPageState::UnPlanned):
            this.enableMoveButtons(false, _selectedJobStatus);
            this.enablePlanButtons(true, _selectedJobStatus);
            currentState = KanbanListPageState::UnPlanned;
            statusQueryStr = queryValue(LeanKanbanJobStatus::NotPlanned);
            break;
        case(KanbanListPageState::Planned):
            this.enableMoveButtons(true, _selectedJobStatus);
            this.enablePlanButtons(false, _selectedJobStatus);
            currentState = KanbanListPageState::Planned;
            statusQueryStr = queryValue(strfmt('!%1', queryValue(LeanKanbanJobStatus::NotPlanned)));
            break;
        case(KanbanListPageState::All):
            this.enableMoveButtons(true, _selectedJobStatus);
            this.enablePlanButtons(true, _selectedJobStatus);
            currentState = KanbanListPageState::All;
            break;
        case(none):
            this.enableMoveButtons(false, _selectedJobStatus);
            this.enablePlanButtons(false, _selectedJobStatus);
            return statusQueryStr; // Exit the method without changing the filter string.
    }
    element.setStatusFilter(statusQueryStr);

    return statusQueryStr;
}

/// <summary>
/// Enables disables the move buttons.
/// </summary>
/// <param name = "_enabled"><c>Boolean</c> indicating if they should be enabled or not.</param>
/// <param name = "_selectedJobStatus">Status of the selected job, from kanban job status.</param>
public void enableMoveButtons(boolean _enabled, LeanKanbanJobStatus _selectedJobStatus)
{
    if (_selectedJobStatus == LeanKanbanJobStatus::NotPlanned || _selectedJobStatus == LeanKanbanJobStatus::Completed)
    {
        _enabled = false;
    }
    Backward.enabled(KanbanJobScheduleOperation::canMoveBackward(KanbanJob) && moveBackwardEnabled ? _enabled : false);
    Forward.enabled(KanbanJobScheduleOperation::canMoveForward(KanbanJob)  && moveForwardEnabled ? _enabled : false);
    MovePreviousPeriod.enabled(_enabled);
    MoveNextPeriod.enabled(_enabled);
    MoveForwardFree.enabled(_selectedJobStatus != LeanKanbanJobStatus::Completed);
}

/// <summary>
/// Enables disables the plan buttons.
/// </summary>
/// <param name = "_enabled"><c>Boolean</c> indicating if they should be enabled or not.</param>
/// <param name = "_selectedJobStatus">Status of the selected job, from kanban job status.</param>
public void enablePlanButtons(boolean _enabled, LeanKanbanJobStatus _selectedJobStatus)
{
    if (_selectedJobStatus != LeanKanbanJobStatus::NotPlanned)
    {
        _enabled = false;
    }
    Plan.enabled(_enabled);
}

