/// <summary>
/// Refreshes the product information fact box if needed.
/// </summary>
/// <remarks>
/// Dynalink will not refresh when we change between lines with the same item id, so we force a refresh.
/// </remarks>
private void refreshProductInformationFactBox()
{
    if (Kanban.ItemId && lastSelectedItemId == Kanban.ItemId)
    {
        FormPartReferenceControl partRefControl = element.getPartControlByName("EcoResProductInformationFactBox");
        partRefControl.refresh();
    }
    lastSelectedItemId = Kanban.ItemId;
}

public int active()
{
    int ret = super();

    element.refreshProductInformationFactBox();

    return ret;
}

