public int active()
{
    int ret;

    ret = super();
    if (!differentStates)
    {
        element.setFormState(currentState, KanbanJob.Status);
    }

    currentReqTrans = KanbanJobScheduleOperation::getReqTransRecord(KanbanJob);

    if (currentReqTrans != null)
    {
        element.enableRequirementsMenu(true);
    }
    else
    {
        element.enableRequirementsMenu(false);
    }

    return ret;
}

