public void clicked()
{
    Set         kanbans = new Set(Types::Int64);//For storing rec ids int64.
    KanbanJob   kanbanJobToMove;
    Args        args = new Args();

    MultiSelectionHelper multiSelectHelper = MultiSelectionHelper::construct();
    multiSelectHelper.parmDatasource(KanbanJob_DS);

    kanbanJobToMove = multiSelectHelper.getFirst();
    while (kanbanJobToMove != null)
    {
        kanbans.add(kanbanJobToMove.Kanban);
        kanbanJobToMove = multiSelectHelper.getNext();
    }
    if (kanbans.elements() > 0)
    {
        args.object(kanbans);
        LeanRuleReassignmentWizard::main(args);

        element.refreshData();
    }
    else
    {
        warning("@SCM:NoKanbans");
    }
}

public void clicked()
{
    MultiSelectionHelper kanbanSelection = MultiSelectionHelper::construct();
    kanbanSelection.parmDataSource(Kanban_DS);

    Kanban kanbanLocal = kanbanSelection.getFirst();
    while (kanbanLocal)
    {
        // Toggle the Priority Selection. The current selection is found in kanbanLocal.Express
        kanbanLocal.setPriority((kanbanLocal.Express == NoYes::Yes) ? NoYes::No : NoYes::Yes);
        kanbanLocal = kanbanSelection.getNext();
    }

    element.refreshData();
}

public void clicked()
{
    const boolean showPrintDialog = true;
    Map cardsByWorkCell = new Map(Types::Int64, Types::Class);

    MultiSelectionHelper kanbanJobSelection = MultiSelectionHelper::construct();
    kanbanJobSelection.parmDataSource(KanbanJob_DS);

    KanbanJob kanbanJobLocal = kanbanJobSelection.getFirst();
    while (kanbanJobLocal)
    {
        if (cardsByWorkCell.exists(kanbanJobLocal.WorkCell))
        {
            List cards = cardsByWorkCell.lookup(kanbanJobLocal.WorkCell);
            cards.addEnd(any2str(kanbanJobLocal.kanbanId()));
        }
        else
        {
            List cards = new List(Types::String);
            cards.addEnd(any2str(kanbanJobLocal.kanbanId()));
            cardsByWorkCell.insert(kanbanJobLocal.WorkCell, cards);
        }

        kanbanJobLocal = kanbanJobSelection.getNext();
    }

    KanbanPrinter::printKanbansByWorkCell(cardsByWorkCell, showPrintDialog, KanbanPrintLocation::WorkCell);
}

