public void dialogClosed(xFormRun _formRun)
{
    Object      dialogForm = _formRun;
    Set         selectedJobIds = new Set(Types::Int64);//For storing rec ids int64.
    KanbanJob   kanbanJobToMove;

    super(_formRun);

    if (_formRun.closedOk())
    {
        MultiSelectionHelper multiSelectHelper = MultiSelectionHelper::construct();
        multiSelectHelper.parmDatasource(KanbanJob_DS);

        kanbanJobToMove = multiSelectHelper.getFirst();
        while (kanbanJobToMove != null)
        {
            selectedJobIds.add(kanbanJobToMove.RecId);
            kanbanJobToMove = multiSelectHelper.getNext();
        }
        KanbanJobAutoPlanForward::planKanbanJobsSet(selectedJobIds,
                                                    dialogForm.fromDate(),
                                                    dialogForm.overrideCapacityShortageReaction(),
                                                    dialogForm.capacityShortageReaction(),
                                                    true);
    }
    element.refreshData();
}

