public void clicked()
{
    super();

    minPlannedDate = element.getPlannedKanbanMinDate(workCellFilterCurrentValue);
    maxPlannedDate = today + endDateBucket;

    PeriodStart.dateValue(minPlannedDate);
    PeriodEnd.dateValue(maxPlannedDate);

    element.setDateRange(minPlannedDate, maxPlannedDate);
}

public void jumpRef()
{
    Args args = new Args();
    args.record(kanban);
    args.caller(this);
    MenuFunction menuFunction = new MenuFunction(menuitemdisplaystr(Kanban), MenuItemType::Display);
    menuFunction.formViewOption(FormViewOption::Details);
    menuFunction.run(args);
}

