public class KanbanQuantityOverview extends FormRun
{
    private KanbanOverviewType                  kanbanOverviewType;
    private KanbanQuantityOverviewShowFilter    showFilter;
    private boolean                             isDisplayChart = false;
    private int                                 refreshCycle;

    private KanbanQuantityOverviewForm      kanbanQuantityOverviewForm;
    private FormButtonManager               formButtonManager;
    private KanbanRulesFormHelper           kanbanRulesFormHelper;
    private SysFormEnumComboBox             kanbanOverviewTypeComboBox;
    private KanbanBoardTmpFilterCriteria    kanbanBoardTmpFilterCriteriaParameter;

    #define.CurrentVersion(1)
    #localmacro.CurrentList
        kanbanBoardTmpFilterCriteriaParameter,
        refreshCycle,
        kanbanOverviewType
    #endmacro

}

public void init()
{
    kanbanQuantityOverviewForm = KanbanQuantityOverviewForm::construct();

    kanbanOverviewTypeComboBox = SysFormEnumComboBox::newParameters(
        element,
        element.controlId(formControlStr(KanbanQuantityOverview, filterKanbanOverviewType)),
        enumnum(KanbanOverviewType),
        KanbanQuantityOverviewForm::allowedKanbanOverviewTypes(),
        "@SCM:KanbanQuantityOverviewType");

    xSysLastValue::getLast(element);

    if (element.args() &&
        element.args().dataset() == tablenum(KanbanBoardTmpFilterCriteria))
    {
        kanbanBoardTmpFilterCriteriaParameter   = element.args().record();
        kanbanOverviewType                      = element.args().parmEnum();
    }

    super();

    kanbanBoardTmpFilterCriteriaParameter.setTmpData(kanbanBoardTmpFilterCriteria);
    kanbanBoardTmpFilterCriteria.data(kanbanBoardTmpFilterCriteriaParameter);

    kanbanBoardTmpFilterCriteria.insert();

    kanbanOverviewTypeComboBox.select(kanbanOverviewType);
    filterWorkCell.text(WrkCtrResourceGroup::find(kanbanBoardTmpFilterCriteria.WorkCell).WrkCtrId);

    this.initializeButtonManager();
    this.updateView();
}

public void close()
{
    super();

    kanbanBoardTmpFilterCriteriaParameter = kanbanBoardTmpFilterCriteria;

    xSysLastValue::saveLast(element);
}

private void initParmDefault()
{
    kanbanOverviewType = kanbanOverviewType::Finished;
}

public int parmRefreshCycle(int _refreshCycle = refreshCycle)
{
    refreshCycle = _refreshCycle;

    return refreshCycle;
}

public void startRefreshCycleTimer()
{
    if (refreshCycle > 0)
    {
        timerRefresh.enabled(true);
    }
    else
    {
        timerRefresh.enabled(false);
    }
}

private void initializeButtonManager()
{
    kanbanRulesFormHelper = KanbanRulesFormHelper::newStandard();

    formButtonManager = FormButtonManager::construct();
    formButtonManager.parmRuleImplementer(kanbanRulesFormHelper);

    formButtonManager.registerButtonRule(appBarButtonGroup, btnKanbanCreate, methodStr(KanbanRulesFormHelper, ruleCheckIsNotNew));
    formButtonManager.registerButtonRule(appBarButtonGroup, btnKanbanCreate, methodStr(KanbanRulesFormHelper, ruleCheckHasKanbanFlow));
    formButtonManager.registerButtonRule(appBarButtonGroup, btnKanbanCreate, methodStr(KanbanRulesFormHelper, ruleCheckIsMakeToStock));
    formButtonManager.registerButtonRule(appBarButtonGroup, btnKanbanCreate, methodStr(KanbanRulesUIHelper,   ruleCheckActiveFlowVersions));
    formButtonManager.registerButtonRule(appBarButtonGroup, btnKanbanCreate, methodStr(KanbanRulesFormHelper, ruleCheckIsActive));
}

private void updateView(boolean _applyShowFilter = false)
{
    filterWorkCell.visible(kanbanOverviewType != KanbanOverviewType::Transfer);
    filterPlanReference.visible(kanbanOverviewType == KanbanOverviewType::Transfer);
    filterPlanActivity.visible(kanbanOverviewType == KanbanOverviewType::Transfer);

    if (_applyShowFilter)
    {
        kanbanQuantityOverviewTmp_DS.applyShowFilter();
    }

    kanbanQuantityOverviewTmp_DS.research(true);

    this.setButtonAccess();
}

private void updateFilters()
{
    kanbanQuantityOverviewTmp_DS.updateKanbanBoardTmpFilterCriteria();
}

private void updateGridVsChartViewMode()
{
    chart.visible(isDisplayChart);
    grid.visible(!isDisplayChart);

    if (isDisplayChart)
    {
        btnSwitchView.text("@SCM:KanbanQuantityOverviewGridViewMode");
        this.setFactBoxesVisible(false);
    }
    else
    {
        btnSwitchView.text("@SCM:KanbanQuantityOverviewChartViewMode");
        this.setFactBoxesVisible(true);
    }

    this.setButtonAccess();
}

private void setFactBoxesVisible(boolean _isVisible)
{
    PartList partList = new PartList(element);

    for (int i = 1; i <= partList.partCount(); i++)
    {
        FormControl formPart = partList.getPartControlById(i);

        if (formPart)
        {
            formPart.visible(_isVisible);
        }
    }
}

private void setButtonAccess()
{
    if (isDisplayChart || !kanbanRule)
    {
        btnKanbanCreate.enabled(false);
        btnPlanMinimum.enabled(false);
        btnPlanMaximum.enabled(false);
    }
    else
    {
        this.updateLeanRuleData();
        formButtonManager.refresh(true);

        btnPlanMinimum.enabled(kanbanQuantityOverviewForm.canPlanMinimum(kanbanQuantityOverviewTmp, kanbanRule));
        btnPlanMaximum.enabled(kanbanQuantityOverviewForm.canPlanMaximum(kanbanQuantityOverviewTmp, kanbanRule));
    }
}

private void updateLeanRuleData()
{
    LeanRuleData leanRuleData = LeanRuleData::newBuffers(kanbanRule,
                                            kanbanRuleFixed,
                                            kanbanRuleVariable,
                                            kanbanRuleEvent,
                                            leanCoverageKanbanRule,
                                            leanCoverage,
                                            kanbanFlow);

    LeanRuleStatusBase::newLeanRuleData(leanRuleData).initFields();

    kanbanRulesFormHelper.initFromLeanRuleData(leanRuleData);
}

private void planKanbanJobs(int _kanbanJobsToPlan)
{
    QueryRun queryRun = KanbanQuantityOverviewTmp::getKanbanJobsQueryRun(kanbanOverviewType, kanbanBoardTmpFilterCriteria, kanbanRule);

    kanbanQuantityOverviewForm.planKanbanJobs(queryRun, _kanbanJobsToPlan);

    element.updateView();
}

