public container pack()
{
    return [#CurrentVersion,#CurrentList];
}

public boolean unpack(container _packedClass)
{
    int     version = RunBase::getVersion(_packedClass);
    boolean result  = true;

    switch (version)
    {
        case #CurrentVersion:
            [version,#CurrentList] = _packedClass;
            break;
        default:
            result = false;
    }

    return result;
}

public void init()
{
    QueryFilter queryFilter;

    super();

    QueryBuildDataSource qbds = this.query().dataSourceTable(tablenum(KanbanQuantityOverviewTmp));

    qbds.addOrderByField(fieldNum(KanbanQuantityOverviewTmp, UrgencyLevel), SortOrder::Descending);
    qbds.addOrderByField(fieldNum(KanbanQuantityOverviewTmp, RuleId), SortOrder::Ascending);

    queryFilter = SysQuery::findOrCreateQueryFilter(this.query(), qbds, fieldStr(KanbanQuantityOverviewTmp, TargetInventSiteId));
    if (kanbanBoardTmpFilterCriteriaParameter.InventSiteIdTo)
    {
        queryFilter.value(kanbanBoardTmpFilterCriteriaParameter.InventSiteIdTo);
    }

    queryFilter = SysQuery::findOrCreateQueryFilter(this.query(), qbds, fieldStr(KanbanQuantityOverviewTmp, TargetInventLocationId));
    if (kanbanBoardTmpFilterCriteriaParameter.InventLocationIdTo)
    {
        queryFilter.value(kanbanBoardTmpFilterCriteriaParameter.InventLocationIdTo);
    }

    queryFilter = SysQuery::findOrCreateQueryFilter(this.query(), qbds, fieldStr(KanbanQuantityOverviewTmp, TargetWMSLocationId));
    if (kanbanBoardTmpFilterCriteriaParameter.WMSLocationIdTo)
    {
        queryFilter.value(kanbanBoardTmpFilterCriteriaParameter.WMSLocationIdTo);
    }

    queryFilter = SysQuery::findOrCreateQueryFilter(this.query(), qbds, fieldStr(KanbanQuantityOverviewTmp, SourceInventSiteId));
    if (kanbanBoardTmpFilterCriteriaParameter.InventSiteIdFrom)
    {
        queryFilter.value(kanbanBoardTmpFilterCriteriaParameter.InventSiteIdFrom);
    }

    queryFilter = SysQuery::findOrCreateQueryFilter(this.query(), qbds, fieldStr(KanbanQuantityOverviewTmp, SourceInventLocationId));
    if (kanbanBoardTmpFilterCriteriaParameter.InventLocationIdFrom)
    {
        queryFilter.value(kanbanBoardTmpFilterCriteriaParameter.InventLocationIdFrom);
    }

    queryFilter = SysQuery::findOrCreateQueryFilter(this.query(), qbds, fieldStr(KanbanQuantityOverviewTmp, SourceWMSLocationId));
    if (kanbanBoardTmpFilterCriteriaParameter.WMSLocationIdFrom)
    {
        queryFilter.value(kanbanBoardTmpFilterCriteriaParameter.WMSLocationIdFrom);
    }
}

public void research(boolean _retainPosition = false)
{
    LeanReplenishmentRuleId currentKanbanRuleId = kanbanQuantityOverviewTmp.RuleId;

    delete_from kanbanQuantityOverviewTmp;

    element.updateFilters();

    kanbanQuantityOverviewTmp.init(kanbanOverviewType, kanbanBoardTmpFilterCriteria);

    this.applyShowFilter();

    super(_retainPosition);

    if (_retainPosition)
    {
        KanbanQuantityOverviewTmp_DS.findValue(fieldNum(KanbanQuantityOverviewTmp, RuleId), currentKanbanRuleId);
    }
}

public void updateKanbanBoardTmpFilterCriteria()
{
    KanbanBoardTmpFilterCriteria.InventSiteIdTo         = this.getFilterValue(this, fieldstr(KanbanQuantityOverviewTmp, TargetInventSiteId));
    KanbanBoardTmpFilterCriteria.InventLocationIdTo     = this.getFilterValue(this, fieldstr(KanbanQuantityOverviewTmp, TargetInventLocationId));
    KanbanBoardTmpFilterCriteria.WMSLocationIdTo        = this.getFilterValue(this, fieldstr(KanbanQuantityOverviewTmp, TargetWMSLocationId));
    KanbanBoardTmpFilterCriteria.InventSiteIdFrom       = this.getFilterValue(this, fieldstr(KanbanQuantityOverviewTmp, SourceInventSiteId));
    KanbanBoardTmpFilterCriteria.InventLocationIdFrom   = this.getFilterValue(this, fieldstr(KanbanQuantityOverviewTmp, SourceInventLocationId));
    KanbanBoardTmpFilterCriteria.WMSLocationIdFrom      = this.getFilterValue(this, fieldstr(KanbanQuantityOverviewTmp, SourceWMSLocationId));
}

public void applyShowFilter()
{
    if (this.queryRun())
    {
        QueryBuildDataSource qbds = this.queryRun().query().dataSourceTable(tablenum(KanbanQuantityOverviewTmp));

        this.clearShowFilterRanges(qbds);

        switch (showFilter)
        {
            case KanbanQuantityOverviewShowFilter::NeedsCreate:
                qbds.addRange(fieldNum(KanbanQuantityOverviewTmp, KanbansToCreate)).value(SysQuery::valueNot(0));
                break;

            case KanbanQuantityOverviewShowFilter::NeedsPlan:
                qbds.addRange(fieldNum(KanbanQuantityOverviewTmp, KanbansToPlan)).value(SysQuery::valueNot(0));
                break;

            case KanbanQuantityOverviewShowFilter::NeedsPrepare:
                qbds.addRange(fieldNum(KanbanQuantityOverviewTmp, KanbansToPrepare)).value(SysQuery::valueNot(0));
                break;

            case KanbanQuantityOverviewShowFilter::NeedsStart:
                qbds.addRange(fieldNum(KanbanQuantityOverviewTmp, KanbansToStart)).value(SysQuery::valueNot(0));
                break;

            case KanbanQuantityOverviewShowFilter::NeedsComplete:
                qbds.addRange(fieldNum(KanbanQuantityOverviewTmp, KanbansToComplete)).value(SysQuery::valueNot(0));
                break;
        }
    }
}

private void clearShowFilterRanges(QueryBuildDataSource _qbds)
{
    _qbds.clearRange(fieldNum(KanbanQuantityOverviewTmp, KanbansToCreate));
    _qbds.clearRange(fieldNum(KanbanQuantityOverviewTmp, KanbansToPlan));
    _qbds.clearRange(fieldNum(KanbanQuantityOverviewTmp, KanbansToPrepare));
    _qbds.clearRange(fieldNum(KanbanQuantityOverviewTmp, KanbansToStart));
    _qbds.clearRange(fieldNum(KanbanQuantityOverviewTmp, KanbansToComplete));
}

private str getFilterValue(FormDataSource _formDataSource, FieldName _fieldName)
{
    str filterValue = '';

    QueryBuildDataSource queryRunQueryBuildDataSource = _formDataSource.queryRunQueryBuildDataSource();

    if (queryRunQueryBuildDataSource)
    {
        QueryFilter queryFilter = SysQuery::findOrCreateQueryFilter(
            _formDataSource.queryRun().query(),
            queryRunQueryBuildDataSource,
            _fieldName);

        if (queryFilter)
        {
            filterValue = this.formatFilterStr(queryFilter.value());
        }
    }

    return filterValue;
}

private str formatFilterStr(str _filterStr)
{
    if (_filterStr == '\"\"')
    {
        _filterStr = '';
    }

    return _filterStr;
}

