public void init()
{
    super();

    QueryBuildDataSource qbds = this.query().dataSourceTable(tablenum(LeanCoverage));

    SysQuery::findOrCreateQueryFilter(this.query(), qbds, fieldStr(LeanCoverage, ItemId));
    SysQuery::findOrCreateQueryFilter(this.query(), qbds, fieldStr(LeanCoverage, AllocationId));
}

public void init()
{
    QueryBuildDataSource qbdsParent, qbdsChild;

    super();

    // define the correct linkage between the InventDim and the LeanCoverage tables
    qbdsParent = this.query().dataSourceTable(tableNum(LeanCoverage));
    qbdsChild = this.query().dataSourceTable(tableNum(InventDim));

    qbdsChild.clearLinks();
    qbdsChild.addLink(fieldNum(LeanCoverage, InventDimId), fieldNum(InventDim, InventDimId));

    InventDimensionEnumerator productDimensions = InventProductDimension::getEnumeratorForEnabledDimensions();
    while (productDimensions.moveNext())
    {
        FieldId   productDimensionFieldId      = productDimensions.currentInventDimFieldId();
        FieldName productionDimensionFieldName = fieldId2Name(tableNum(InventDim), productDimensionFieldId);

        SysQuery::findOrCreateQueryFilter(this.query(), qbdsChild, productionDimensionFieldName);
    }
}

