public void dialogClosed(xFormRun _formRun)
{
    Object  object;

    super(_formRun);

    if (_formRun.closedOk())
    {
        object = _formRun;
        refreshCycle = object.parmRefreshCycle();
    }

    element.startRefreshCycleTimer();
}

