public boolean modified()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        if (this.text())
        {
            ret = kanbanQuantityOverviewForm.isWorkCellFilterValid(this.text());

            if (ret)
            {
                kanbanBoardTmpFilterCriteria.WorkCell = WrkCtrResourceGroup::findWrkCtrId(this.text()).RecId;
            }
        }
        else
        {
            kanbanBoardTmpFilterCriteria.WorkCell = 0;
        }

        if (ret)
        {
            element.updateView();
        }
    }

    return ret;
}

public Common lookupReference()
{
    Query                   query  = new Query();
    SysReferenceTableLookup lookup = SysReferenceTableLookup::newParameters(tablenum(PlanReference),this,true);
    QueryBuildDataSource    queryBuildDataSource;

    CompanyInfo companyInfo = CompanyInfo::find();

    queryBuildDataSource = query.addDataSource(tablenum(PlanReference));
    queryBuildDataSource.addRange(fieldnum(PlanReference,LegalEntity)).value(queryValue(companyInfo.RecId));

    lookup.addLookupfield(fieldnum(PlanReference, PlanName));
    lookup.addLookupfield(fieldnum(PlanReference, PlanDescription));
    lookup.parmQuery(query);

    return lookup.performFormLookup();
}

public Common lookupReference()
{
    Query                   query  = new Query();
    SysReferenceTableLookup lookup = SysReferenceTableLookup::newParameters(tablenum(PlanActivity),this,true);
    QueryBuildDataSource    queryBuildDataSource;

    CompanyInfo companyInfo = CompanyInfo::find();

    queryBuildDataSource = query.addDataSource(tablenum(PlanActivity));
    queryBuildDataSource.addRange(fieldnum(PlanActivity,PlanActivityType)).value(queryValue(PlanActivityType::Transfer));
    queryBuildDataSource.addRange(fieldnum(PlanActivity,LegalEntity)).value(queryValue(companyInfo.RecId));

    if (KanbanBoardTmpFilterCriteria.PlanReference)
    {
        queryBuildDataSource.addRange(fieldnum(PlanActivity,PlanReference)).value(queryValue(KanbanBoardTmpFilterCriteria.PlanReference));
    }

    lookup.addLookupfield(fieldnum(PlanActivity,Name));
    lookup.addLookupfield(fieldnum(PlanActivity,PlanActivityId));
    lookup.addLookupMethod(tablemethodstr(PlanActivity,planReferenceName));
    lookup.setLabel("@SYS314196");
    lookup.parmQuery(query);

    return lookup.performFormLookup();
}

