public void autoPlanRules()
{
    // get the first selected kanban rule in the grid
    KanbanRule selectedKanbanRule =  kanbanRule_ds.getFirst(1) ?
                                     kanbanRule_ds.getFirst(1) :
                                     kanbanRule_ds.cursor();

    // move over all selected ones (multiselection allowed) and print them
    while (selectedKanbanRule)
    {
        LeanRulesEngine::autoPlan(selectedKanbanRule.ruleData());

        selectedKanbanRule = kanbanRule_ds.getNext();
    }
}

public void close()
{
    super();

    if (refreshParent &&
        formHasMethod(element.args().caller(),identifierStr(refreshQuantityControl)))
    {
        element.args().caller().refreshQuantityControl(true);
    }
}

