    this.initializeButtonManager();

    // update the controls that are on the screen
    this.updateDesign(InventDimFormDesignUpdate::Init);
}

public void applyFilter()
{
    this.parmKanbanBoardTmpFilterCriteria().PlanReference = planReference.value();
    this.parmKanbanBoardTmpFilterCriteria().PlanActivity = lastPlanActivity.value();
    this.parmShowOnlyActive(showOnlyActiveRules.value());

    this.initFilter();
}

public void initFilter()
{
    str activeDateTime;

    // Production flow filter
    if (kanbanBoardTmpFilterCriteria.PlanReference)
    {
        qbrKanbanProductionFlow.value(SysQuery::value(kanbanBoardTmpFilterCriteria.PlanReference));
    }
    else
    {
        qbrKanbanProductionFlow.value(SysQuery::valueUnlimited());
    }
    // Last activity filter
    if (kanbanBoardTmpFilterCriteria.PlanActivity)
    {
        qbrKanbanLastPlanActivity.value(SysQuery::value(kanbanBoardTmpFilterCriteria.PlanActivity));
    }
    else
    {
        qbrKanbanLastPlanActivity.value(SysQuery::valueUnlimited());
    }

    // Show only active rules filter
    if (showOnlyActive)
    {
        activeDateTime = DateTimeUtil::toStr(DateTimeUtil::getSystemDateTime());
        // Has started and has not ended
        qbrActiveKanbanRules.value(strFmt('((%1.%2 <= %3) && ((%4.%5 >= %6) || (%7.%8 = %9)))',
            tableStr(LeanCoverageKanbanRule), fieldStr(LeanCoverageKanbanRule, ValidFrom), activeDateTime,
            tableStr(LeanCoverageKanbanRule), fieldStr(LeanCoverageKanbanRule, ValidTo), activeDateTime,
            tableStr(LeanCoverageKanbanRule), fieldStr(LeanCoverageKanbanRule, ValidTo), #utcDateTimeNull));
    }
    else
    {
        qbrActiveKanbanRules.value(SysQuery::valueUnlimited());
    }

    // Refresh kanban rule grid
    this.updateLeanRuleData(true);
    this.refreshKanbanRulesGrid();
}

/// <summary>
/// Initializes the form from its caller. If a <c>KanbanRule</c> record is passed as an argument
/// it is used to filter the records for this particular rule.
/// In case the form is called from planned order form and <c>ReqPO</c> datasource is passed as an argument
/// a related <c>KanbanRule</c> record is found and used as the filter.
/// </summary>
private void initFromCaller()
{
    Args            args = element.args();
    ReqPO           reqPO;
    ReqSetupDim     reqSetupDim;
    LeanRuleData    ruleData;

    if ( args.record() is ReqPO )
    {
        reqPO = args.record();

        reqSetupDim = reqPO.reqSetupDim();
        ruleData    = reqSetupDim.leanRuleData(reqPO.reqDateTime(),reqPO.RefType,reqPO.Qty);

        if (ruleData
            && ruleData.parmKanbanRule().RecId != 0)
        {
            filterKanbanRule = ruleData.parmKanbanRule();
        }
        else
        {
            throw error("@SYS309574");
        }
    }
    else
    {
        filterKanbanRule = args.record();
    }
}

