/// <summary>
/// Handles the change in a field's value.
/// </summary>
/// <param name="_tableId">
/// The id of the table which contains the updated field.
/// </param>
/// <param name="_fieldId">
/// The id of the field which value has changed.
/// </param>
public void modifiedField(
    TableId _tableId,
    FieldId _fieldId)
{
    leanRuleData.type().modifiedField(_tableId, _fieldId);
}

public boolean modifiedPlanPlanActivity(KanbanFlowElementRole _role)
{
    boolean ret = kanbanRulesFormHelper.validatePlanPlanActivity(_role);

    if (ret)
    {
        element.onKanbanFlowChanged();
        element.updateDesign();
    }

    // given that the onKanbanFlowChanged() handler might result in changing both activities,
    // (i.e. the complete kanban flow), we refresh both cached methods
    kanbanFlow_ds.cacheCalculateMethod(tableMethodStr(KanbanFlow, editPlanActivityNameFirst));
    kanbanFlow_ds.cacheCalculateMethod(tableMethodStr(KanbanFlow, editPlanActivityNameLast));
    // update the controls in case cached data changed because of the above cache refresh
    preview_FirstPlanPlanActivity.update();
    preview_LastPlanPlanActivity.update();

    this.setButtonAccess();

    return ret;
}

/// <summary>
/// Opens the LeanProductionFlow form for the element's parent LeanProductionFlow.
/// </summary>
/// <param name="_plan">
/// The plan that should be opened in the form.
/// </param>
public void navigateToProductionFlow(Plan _plan)
{
    Args                args;

    args = new Args();
    args.caller(this);
    args.record(_plan);

    new MenuFunction(menuitemDisplayStr(LeanProductionFlow), MenuItemType::Display).run(args);
}

NumberSeqFormHandler numberSeqFormHandler()
{
    // Activate number sequence handler for the form
    if (!numberSeqFormHandler)
    {
        numberSeqFormHandler = NumberSeqFormHandler::newForm(ProdParameters::numRefKanbanRuleId().NumberSequenceId,
                                                             element,
                                                             kanbanRule_ds,
                                                             fieldNum(KanbanRule, RuleId));
    }

    return numberSeqFormHandler;
}

/// <summary>
///    Handles changes in the kanban flow.
/// </summary>
/// <param name="_setMultiActivityFlag">
///     Indicates if the multi activity flag should be passed to the handler.
/// </param>
/// <param name="_multiActivityFlag">
///     Indicates the state of the edit activity flag control.
/// </param>
public void onKanbanFlowChanged(
    boolean _setMultiActivityFlag = false,
    boolean _multiActivityFlag    = false)
{
    if (leanRuleData)
    {
        // let the KanbanFlowWrapper handle the change in the kanban flow
        // eventually showing the KanbanFlowSelection dialog
        if (!_setMultiActivityFlag)
        {
            leanRuleData.parmKanbanFlowWrapper().onKanbanFlowChanged(leanRuleData);
        }
        else
        {
            leanRuleData.parmKanbanFlowWrapper().onKanbanFlowChanged(leanRuleData, _multiActivityFlag);
        }
    }
}

