/// <summary>
/// Refreshes the data sources that are dependent on a change in the KanbanRule and
/// its related and inner-joined data sources.
/// </summary>
public void refreshDataSources()
{
    this.refreshKanbanGrid();

    kanbanRule_ds.refresh();
    kanbanRuleFixed_ds.refresh();
    kanbanRuleVariable_ds.refresh();
    kanbanRuleEvent_ds.refresh();
    leanCoverage_ds.refresh();
    leanCoverageKanbanRule_ds.refresh();
    kanbanFlow_ds.refresh();
    leanCoverage_InventDim_ds.refresh();
    kanban_ds.refresh();
}

/// <summary>
/// Refreshes the grid with kanbans and updates the UI.
/// </summary>
public void refreshKanbanGrid()
{
    // if showEmpty is false we only show not-empty kanbans, otherwise no filtering is done
    qbrKanbanStatus.value(showEmptyFlag ? SysQuery::valueUnlimited() :
                          SysQuery::valueNot(LeanKanbanStatus::Emptied));

    // filter the Kanban data source
    qbrKanbanRule.value(SysQuery::value(kanbanRule.RecId ? kanbanRule.RecId : -1));
    kanban_ds.executeQuery();
    kanban_ds.refresh();
}

/// <summary>
/// Refreshes the grid with KanbanRules and updates the UI.
/// </summary>
/// <remarks>
/// The method prevents recursive calls to itself during data persistence,
/// so it can be called from within CRUD overrides.
/// </remarks>
public void refreshKanbanRulesGrid()
{
    KanbanRule currKanbanRule;

    // if we're already in the process of refreshing we do nothing
    // (avoiding recursion and undetermined datasource state when in between
    //  write -> leaveRecord -> ... )
    if (inRefresh)
    {
        return;
    }

    try
    {
        inRefresh = true;
        currKanbanRule = kanbanRule.data();

        kanbanRule_ds.executeQuery();
        kanbanRule_ds.positionToRecord(currKanbanRule);

        // update form controls
        element.updateDesign();
        inRefresh = false;
    }
    catch
    {
        // the state flag should be reset also in case of an intermediate
        // exception being thrown
        inRefresh = false;
    }
}

/// <summary>
/// Resets the item dimensions in the InventDim buffer when a new Item is selected
/// </summary>
public void resetInventDim()
{
    InventDim newInventDim;

    // restore the coverage dimensions
    newInventDim.InventSiteId       = leanCoverage_InventDim.InventSiteId;
    newInventDim.InventLocationId   = leanCoverage_InventDim.InventLocationId;
    newInventDim.wmsLocationId      = leanCoverage_InventDim.wmsLocationId;

    leanCoverage_InventDim.data(InventDim::findOrCreate(newInventDim));
    leanCoverage.InventDimId = leanCoverage_InventDim.InventDimId;

    leanCoverage_ds.cacheCalculateMethod(tableMethodStr(LeanCoverage,productSelectionString));
}

