{
    if (formButtonManager)
    {
        formButtonManager.refresh(true);
    }
}

/// <summary>
/// The method sets the current records for the form.
/// </summary>
/// <param name="_modifiedData">
/// The data that was modified.
/// </param>
/// <remarks>
/// This method should be called after data modifications in order
/// to keep the UI in sync with the data structures.
/// </remarks>
public void setModifiedDatasourcesAsCurrent(LeanRuleDataUpdate _modifiedData)
{
    kanbanRule.data(_modifiedData.parmKanbanRule());
    kanbanRule_ds.setCurrent();

    kanbanRuleFixed.data(_modifiedData.parmKanbanRuleFixed());
    kanbanRuleFixed_ds.setCurrent();

    kanbanRuleEvent.data(_modifiedData.parmKanbanRuleEvent());
    kanbanRuleEvent_ds.setCurrent();

    kanbanRuleVariable.data(_modifiedData.parmKanbanRuleVariable());
    kanbanRUleVariable_ds.setCurrent();

    leanCoverageKanbanRule.data(_modifiedData.parmLeanCoverageKanbanRule());
    leanCoverageKanbanRule_ds.setCurrent();

    leanCoverage.data(_modifiedData.parmLeanCoverage());
    leanCoverage_ds.setCurrent();

    kanbanFlow.data(_modifiedData.kanbanFlow());
    kanbanFlow_DS.setCurrent();
}

/// <summary>
/// Shows the KanbanRuleDuplicate form.
/// </summary>
/// <param name="_formMode">
/// The form mode.
/// </param>
/// <remarks>
/// The form is shown as a modal dialog.
/// </remarks>
public void showKanbanDuplicateForm(KanbanRuleDuplicateFormMode _formMode)
{
    Args        args = new Args();
    FormRun     formRun;

    // create the args
    args.name(formStr(KanbanRuleDuplicate));
    args.caller(this);
    args.parmEnumType(enumNum(KanbanRuleDuplicateFormMode));
    args.parmEnum(_formMode);
    args.record(kanbanRule);

    // Menu item info will be used by security framework, when setting access rights to form datasources
    args.menuItemType(MenuItemType::Action);
    args.menuItemName(menuitemActionStr(KanbanRuleDuplicate));

    // create a form run and initialize it
    formRun = classfactory.formRunClass(args);

    formRun.init();
    formRun.run();
    formRun.wait(true);
}

