/// <summary>
/// Updates the controls visible in the form.
/// </summary>
/// <param name="_mode">
/// Indicates the type of update that is taking place.
/// </param>
public void updateDesign(InventDimFormDesignUpdate _mode = InventDimFormDesignUpdate::Active)
{
    InventDimParm       inventDimParmEnabled;

    // refresh all buttons that are registered in the buttons manager
    this.setButtonAccess();

    // refresh all buttons which state depends on the checkbox group controls
    this.updateControlsFromData();

    // maintain the mandatoriness of the form's fields
    this.updateMandatoryFields();

    // refresh the InventDim fields
    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init :
            if (!inventDimFormSetup)
            {
                inventDimFormSetup = InventDimCtrl_Frm_ReqItemTable::newFromForm(element);
            }
            InventDimParm   inventDimParmLocked;
            inventDimParmLocked.initProductDimensionsAllGroups();
            inventDimParmLocked.toggleAllInventDim();
            inventDimFormSetup.parmDimParmLockedRightClick(inventDimParmLocked);

        //fall through
        case InventDimFormDesignUpdate::Active,
             InventDimFormDesignUpdate::FieldChange:
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemId(leanCoverage.ItemId));
            InventDimGroupSetup::newItemId(leanCoverage.ItemId).inventDimParmActiveProductDimension(inventDimParmEnabled);
            inventDimFormSetup.parmDimParmEnabled(inventDimParmEnabled);
            inventDimFormSetup.parmMarkFieldAsMandatory(   leanCoverage.RuleCoverageType    == LeanRuleCoverageType::Product
                                                        && kanbanRule.ReplenishmentStrategy == LeanReplenishmentStrategy::Fixed);
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::SetupChange:
            break;

        default :
            // Incorrect parameter for the function '%1'
            throw error(strFmt("@SYS54195", funcName()));
    }
}

/// <summary>
/// Updates the LeanRuleData object that is maintained by the form.
/// </summary>
/// <param name="_reset">
/// The flag indicating if the LeanRuleData object should be reset.
/// </param>
public void updateLeanRuleData(boolean _reset = false)
{
    if (_reset)
    {
        leanRuleData = null;
    }
    else
    {
        leanRuleData = LeanRuleData::newBuffers(kanbanRule,
                                            kanbanRuleFixed,
                                            kanbanRuleVariable,
                                            kanbanRuleEvent,
                                            leanCoverageKanbanRule,
                                            leanCoverage,
                                            kanbanFlow);

        LeanRuleStatusBase::newLeanRuleData(leanRuleData).initFields();

        leanRuleData.parmInventDim(leanCoverage_inventDim);
    }

    // propagate the changes to the helper class
    kanbanRulesFormHelper.initFromLeanRuleData(leanRuleData);
}

/// <summary>
/// Maintains the dynamic mandatory flags of the fields on the form.
/// </summary>
/// <remarks>
/// Some of the fields are not marked as mandatory due to denormalization. Hence
/// we need to manually handle those in order to get the framework support in place.
/// </remarks>
public void updateMandatoryFields()
{
    // default quantity and fixed kanban quantity are mandatory for fixed rules
    kanbanRule_ds.object(fieldNum(KanbanRule, Quantity)).mandatory(kanbanRule.ReplenishmentStrategy == LeanReplenishmentStrategy::Fixed);
    kanbanRuleFixed_ds.object(fieldNum(KanbanRuleFixed, Level)).mandatory(kanbanRule.ReplenishmentStrategy == LeanReplenishmentStrategy::Fixed);

    // the item relation FKs depend on the rule coverage type
    leanCoverage_ds.object(fieldNum(LeanCoverage, ItemId)).mandatory(leanCoverage.RuleCoverageType == LeanRuleCoverageType::Product);
    leanCoverage_ds.object(fieldNum(LeanCoverage, AllocationId)).mandatory(leanCoverage.RuleCoverageType == LeanRuleCoverageType::ProductFamily);

    // the circulating card related fields depend on the state of the check box
    kanbanRule_ds.object(fieldNum(KanbanRule, CardsNumberSequence)).mandatory(kanbanRule.UsesCirculatingCards == NoYes::Yes);
    kanbanRule_ds.object(fieldNum(KanbanRule, CardAssignmentType)).mandatory(kanbanRule.UsesCirculatingCards == NoYes::Yes);

    // the last activity field is mandatory only if the flow is in multi-activity mode
    preview_LastPlanPlanActivity.mandatory(kanbanRulesFormHelper.isMultiActivityKanbanFlow());
    grid_LastPlanPlanActivity.mandatory(false);
}

public void init()
{
    QueryBuildDataSource    qbds;

    super();

    qbds = this.query().dataSourceTable(tableNum(KanbanRule));

    // clear external dependencies
    qbds.clearDynalinks();

    // maintain correct ordering of rules in the grid
    qbds.addOrderByField(fieldNum(KanbanRule, RuleId), SortOrder::Descending);

    // handle pre-filtering of the grid if the form is opened for a particular rule
    if (filterKanbanRule.RecId != 0)
    {
        qbds.addRange(fieldNum(KanbanRule, RecId)).value(SysQuery::value(filterKanbanRule.RecId));
    }

    // get a range for the kanban's active date
    qbrActiveKanbanRules = qbds.addRange(fieldNum(KanbanRule, RecId));

    // show only rules in current legal entity
    qbds.addRange(fieldNum(KanbanRule,LegalEntity)).value(queryValue(CompanyInfo::current()));
    qbds.addRange(fieldNum(KanbanRule, RuleId));
}

public boolean validateWrite()
{
    LeanRuleStatusBase leanRuleStatus = LeanRuleStatusBase::newLeanRuleData(leanRuleData, kanbanRule);

    boolean ret = true;

    // check the number sequence
    ret = ret && element.numberSeqFormHandler().formMethodDataSourceValidateWrite(ret);

    // do the extensive rule-type specific validation
    ret = leanRuleStatus.validateWrite(true) && ret;

    return ret;
}

public void write()
{
    LeanRuleDataUpdate leanRuleDataUpdate;

    // create the inventDim and link it to the LeanCoverage buffer
    LeanCoverage_InventDim.data(leanCoverage.setInventDimId(leanCoverage_InventDim));

    element.numberSeqFormHandler().formMethodDataSourceWrite();

    // get an extended LeanRuleData that contains the value of the Replaced rule field
    // that is used by the LeanCommandUpdateRule
    leanRuleDataUpdate = LeanRuleDataUpdate::newStandard(leanRuleData, replacedRuleId);

    // the actual validation and persistence is managed by the rules engine and is status dependent
    if (LeanRuleStatusBase::newLeanRuleData(leanRuleDataUpdate).write(leanRuleDataUpdate))
    {
        element.setModifiedDatasourcesAsCurrent(leanRuleDataUpdate);
        element.updateLeanRuleData();

        kanbanFlow_ds.cacheCalculateMethod(tableMethodStr(KanbanFlow, editPlanActivityNameFirst));
        kanbanFlow_ds.cacheCalculateMethod(tableMethodStr(KanbanFlow, editPlanActivityNameLast));

        element.setButtonAccess();
        KanbanRule_DS.reread();
    }
}

public int active()
{
    int ret;

    ret = super();

    // update the ruleId for the new record
    replacedRuleId = kanbanRule.replacedRule().RuleId;

    // propagate the record change to the helper class
    kanbanRulesFormHelper.activate();

    // update the lean rule data object
    element.updateLeanRuleData();

    // update the controls on the screen
    element.updateDesign();

    // refresh the data source that are dependent on the currently selected rule
    element.refreshDataSources();

    return ret;
}

public void delete()
{
    // handle number sequences
    element.numberSeqFormHandler().formMethodDataSourceDelete();

    element.updateLeanRuleData(true);

    super();

    // if there're no records left we should clear the buffers
    if (!kanbanRule_ds.getFirst())
    {
        element.refreshDataSources();
        kanbanRule_ds.research();
    }

    // refresh UI
    element.updateDesign();
}

public void create(boolean _append = false)
{
    // handle number sequences
    if (KanbanRule::checkNumberSeq())
    {
        element.numberSeqFormHandler().formMethodDataSourceCreatePre();

        super(_append);

        // make sure the LeanRuleData object in the form is in sync with the newly
        // created buffers
        element.updateLeanRuleData();

        element.numberSeqFormHandler().formMethodDataSourceCreate();
    }

    preview_KanbanType.setFocus();
}

/// <summary>
/// Edit method for the replacing rule's ID.
/// </summary>
/// <param name="_set">
/// The parameter that identifies if the value is to be set or retrieved.
/// </param>
/// <param name="_leanReplenishmentRuleId">
/// The LeanReplenishmentRuleId that is to be set or retrieved.
/// </param>
/// <returns>
/// The LeanReplenishmentRuleId of the destination location.
/// </returns>
public edit LeanReplenishmentRuleId replacingRuleId(
    boolean                 _set,
    LeanReplenishmentRuleId _leanReplenishmentRuleId)
{
    if (_set)
    {
        replacedRuleId = _leanReplenishmentRuleId;

        // propagate the value to the helper class
        kanbanRulesFormHelper.onNewReplacedRuleId(_leanReplenishmentRuleId);
        element.updateDesign();
    }
    else
    {
        _leanReplenishmentRuleId = replacedRuleId;
    }

    return _leanReplenishmentRuleId;
}

