public void clicked()
{
    FormRun formRun;
    Object kanbanFlowSelectionForm;
    Args args;

    // instantiate the form
    args = new Args();
    args.name(formStr(KanbanFlowSelection));
    formRun = classfactory.formRunClass(args);

    // set the LeanRuleData object we're operating on
    kanbanFlowSelectionForm = formRun;
    kanbanFlowSelectionForm.parmLeanRuleData(leanRuleData);

    // show the form as a modal dialog
    formRun.init();
    formRun.run();
    formRun.wait(true);

    // if the form was closed with OK (a flow selected), then
    // we need to refresh the form controls
    if (formRun.closedOk())
    {
        // have to trigger a recalculation of the edit fields
        kanbanFlow_ds.cacheCalculateMethod(tableMethodStr(KanbanFlow, editPlanActivityNameFirst));
        kanbanFlow_ds.cacheCalculateMethod(tableMethodStr(KanbanFlow, editPlanActivityNameLast));

        element.updateDesign();
    }
}

