    // refresh the grids after creating the kanbans
    element.refreshDataSources();

    // refresh controls
    element.updateDesign();
}

public void clicked()
{
    // get the first selected kanban in the grid
    Kanban selectedKanban = kanban_ds.getFirst(true);

    // in case nothing is marked (no multi-selection done), we delete the
    // current record. otherwise UX is confusing
    if (!selectedKanban)
    {
        selectedKanban = kanban;
    }

    KanbanMultiDelete::runFromFormActiveKanban(selectedKanban);

    // refresh the grids
    element.refreshDataSources();

    // refresh controls
    element.updateDesign();
}

public void dialogClosed(xFormRun _formRun)
{
    super(_formRun);

    element.refreshKanbanGrid();
}

public void clicked()
{
    kanbanRulesFormHelper.handlePrintKanbans(kanban_ds);
}

public Common lookupReference()
{
    Query                   query  = new Query();
    SysReferenceTableLookup lookup = SysReferenceTableLookup::newParameters(tablenum(PlanReference),this,true);
    QueryBuildDataSource    queryBuildDataSource;

    queryBuildDataSource = query.addDataSource(tablenum(PlanReference));
    queryBuildDataSource.addRange(fieldnum(PlanReference,LegalEntity)).value(queryValue(CompanyInfo::current()));

    lookup.addLookupfield(fieldnum(PlanReference,PlanName));
    lookup.addLookupfield(fieldnum(PlanReference,PlanDescription));
    lookup.parmQuery(query);

    return lookup.performFormLookup();
}

public boolean modified()
{
    boolean ret = super();

    // when change Production flow, clean Last activity filter as otherwise user is always presented with no kanban rules matching a criteria and anyway deletes Last activity filter manually.
    lastPlanActivity.value(0);

    element.applyFilter();

    return ret;
}

public Common lookupReference()
{
    Query                   query                   = new Query();
    SysReferenceTableLookup lookup                  = SysReferenceTableLookup::newParameters(tablenum(PlanActivity),this,true);
    QueryBuildDataSource    queryBuildDataSource;

    queryBuildDataSource = query.addDataSource(tablenum(PlanActivity));
    queryBuildDataSource.addRange(fieldnum(PlanActivity,LegalEntity)).value(queryValue(CompanyInfo::current()));

    if (kanbanBoardTmpFilterCriteria.PlanReference)
    {
        queryBuildDataSource.addRange(fieldnum(PlanActivity,PlanReference)).value(queryValue(kanbanBoardTmpFilterCriteria.PlanReference));
    }

    queryBuildDataSource.addRange(fieldnum(PlanActivity,PlanActivityType)).value(queryValue(PlanActivityType::Process));
    queryBuildDataSource.addRange(fieldnum(PlanActivity,PlanActivityType)).value(queryValue(PlanActivityType::Transfer));

    lookup.addSelectionField(fieldnum(PlanActivity,Name));
    lookup.addLookupfield(fieldnum(PlanActivity,Name));
    lookup.addLookupfield(fieldnum(PlanActivity,PlanActivityId));
    lookup.addLookupMethod(tablemethodstr(PlanActivity,planReferenceName));
    lookup.setLabel("@SYS314196");
    lookup.parmQuery(query);

    return lookup.performFormLookup();
}

public boolean modified()
{
    boolean ret = super();

    element.applyFilter();

    return ret;
}

public boolean modified()
{
    boolean ret = super();

    element.applyFilter();

    return ret;
}

public void jumpRef()
{
    if (this.valueStr())
    {
        PlanActivity::jumpRefPlanActivity(kanbanFlow.firstPlanActivity(),this);
    }
}

public void lookup()
{
    kanbanRulesFormHelper.lookupPlanPlanActivity(this, this.text(), KanbanFlowElementRole::Source);
}

public boolean modified()
{
    return super()
            && element.modifiedPlanPlanActivity(KanbanFlowElementRole::Source);
}

public void jumpRef()
{
    if (this.valueStr())
    {
        PlanActivity::jumpRefPlanActivity(kanbanFlow.lastPlanActivity(),this);
    }
}

public void lookup()
{
    kanbanRulesFormHelper.lookupPlanPlanActivity(this, this.text(), KanbanFlowElementRole::Destination);
}

public boolean modified()
{
    return    super()
           && element.modifiedPlanPlanActivity(KanbanFlowElementRole::Destination);
}
