[Form]
public class LeanProductionFlow extends FormRun
{
    [FormObservable]
    LeanProductionFlow              activeVersion;
    ProdParameters                  prodParameters;
    CompanyInfo                     companyInfo;

}

public display LeanAverageTakt activeVersionAverageTaktTime()
{
    return activeVersion.AverageTakt;
}

public void init()
{
    super();

    companyInfo    = CompanyInfo::find();
    prodParameters = ProdParameters::find();
}

public void setFormControls(boolean _on = true)
{
    boolean showField            = plan.RecId != 0 && _on;
    boolean isCurrentLegalEntity = planReference.RecId == 0 || planReference.LegalEntity == companyInfo.RecId;

    // set data sources and 'edit' type menu items to read-only if the selected production flow belongs to a different legal entity
    planReference_ds.allowDelete(isCurrentLegalEntity);
    planReference_ds.allowEdit(isCurrentLegalEntity);
    leanProductionFlowReference_ds.allowDelete(isCurrentLegalEntity);
    leanProductionFlowReference_ds.allowEdit(isCurrentLegalEntity);
    plan_ds.allowDelete(isCurrentLegalEntity);
    plan_ds.allowEdit(isCurrentLegalEntity);
    plan_ds.allowCreate(isCurrentLegalEntity);

    transactionsButtonGroup.enabled(isCurrentLegalEntity);
    calculationButtonGroup.enabled(isCurrentLegalEntity);

    newVersion.enabled(isCurrentLegalEntity && planReference.RecId != 0);
    deleteCommandButton.enabled(showField);

    activationVersionButtonGroup.enabled(isCurrentLegalEntity && showField);

    activitiesButtonGroup.enabled(showField);
}

public void showNewVersionDialog()
{
    Dialog          dialog = new Dialog("@SYS132281"); // Create new production flow version

    DialogField     dialogOldProductionFlow;
    DialogField     dialogOldProductionFlowVersion;
    DialogField     dialogOldFromDate;
    DialogField     dialogOldToDate;
    DialogField     dialogCopyFromVersion;

    DialogField     dialogNewProductionFlow;
    DialogField     dialogNewFromDate;
    DialogField     dialogNewToDate;

    DialogField     dialogDuplicateKanbanRules;

    Timezone        userTimeZone = DateTimeUtil::getUserPreferredTimeZone();

    boolean         duplicateKanbanRules = false;

    if (!prodParameters.ProductionFlowDefaultTimeUnit || !prodParameters.ProductionFlowDefaultQuantityUnit)
    {
        // Default values for production flow time unit and quantity unit must be specifed in the production parameters
        throw error("@SYS327547");
    }

    if (plan.RecId)
    {
        // Copy from version
        dialog.addGroup("@SYS117843");

        dialogOldProductionFlow = dialog.addField(extendedTypeStr(PlanName), "@SYS131941");
        dialogOldProductionFlow.value(planReference.PlanName);
        dialogOldProductionFlow.allowEdit(false);
        dialogOldProductionFlow.skip(true);

        dialogOldProductionFlowVersion = dialog.addField(extendedTypeStr(PlanVersionNum));
        dialogOldProductionFlowVersion.value(plan.VersionNum);
        dialogOldProductionFlowVersion.allowEdit(false);
        dialogOldProductionFlowVersion.skip(true);

        dialogOldFromDate = dialog.addField(extendedTypeStr(PlanValidFromDateTime));
        dialogOldFromDate.value(DateTimeUtil::applyTimeZoneOffset(plan.ValidFrom, userTimeZone));
        dialogOldFromDate.allowEdit(false);
        dialogOldFromDate.skip(true);

        dialogOldToDate = dialog.addField(extendedTypeStr(PlanValidToDateTime));
        dialogOldToDate.value(DateTimeUtil::applyTimeZoneOffset(plan.ValidTo, userTimeZone));
        dialogOldToDate.allowEdit(false);
        dialogOldToDate.skip(true);

        dialogCopyFromVersion = dialog.addField(extendedTypeStr(NoYesId), "@SYS117843", "@SYS340116");
        dialogCopyFromVersion.value(NoYes::Yes);
    }

    // New version
    dialog.addGroup("@SYS132280");

    dialogNewProductionFlow = dialog.addField(extendedTypeStr(PlanName), "@SYS131941");
    dialogNewProductionFlow.value(planReference.PlanName);
    dialogNewProductionFlow.allowEdit(false);
    dialogNewProductionFlow.skip(true);

    dialogNewFromDate = dialog.addField(extendedTypeStr(PlanValidFromDateTime));
    if (plan.RecId
        && plan.ValidTo
        && plan.ValidTo < DateTimeUtil::maxValue())
    {
        dialogNewFromDate.value(DateTimeUtil::applyTimeZoneOffset(DateTimeUtil::addSeconds(plan.ValidTo,1), userTimeZone));
    }
    else
    {
        dialogNewFromDate.value(DateTimeUtil::applyTimeZoneOffset(DateTimeUtil::getSystemDateTime(), userTimeZone));
    }

    dialogNewToDate = dialog.addField(extendedTypeStr(PlanValidToDateTime));

    if (plan.RecId)
    {
        // Parameters
        dialog.addGroup("@SYS7764");

        // Duplicate kanban rules
        dialogDuplicateKanbanRules = dialog.addField(enumStr(NoYes), "@SYS132283", "@SYS340115");
        dialogDuplicateKanbanRules.value(true);
    }

    if (dialog.run())
    {
        // Should activities be copied from the old version?
        if (dialogCopyFromVersion)
        {
            if (dialogCopyFromVersion.value())
            {
                // If activities are not copied, the kanban rules should not be copied
                duplicateKanbanRules = dialogDuplicateKanbanRules.value();
            }
            else
            {
                plan.RecId = 0;
            }
        }

        // Create a new production flow version based on the selections made in the dialog
        LeanProductionFlow::createNewVersion(planReference.RecId,
                                             plan.RecId,
                                             DateTimeUtil::removeTimeZoneOffset(dialogNewFromDate.value(), userTimeZone),
                                             DateTimeUtil::removeTimeZoneOffset(dialogNewToDate.value(), userTimeZone),
                                             duplicateKanbanRules);

        plan_ds.executeQuery();
    }
}

public boolean validateWrite()
{
    boolean     ret = super();
    if (ret)
    {
        ret = leanProductionFlowReference.validateField(fieldNum(LeanProductionFlowReference,ProdGroupId));
    }

    return ret;
}

public int active()
{
    int ret = super();
    if (ret)
    {
        activeVersion = LeanProductionFlow::findPlan(Plan::findActive(planReference.RecId).RecId);

        element.setFormControls(false);

        CompanyInfo company = CompanyInfo::findRecId(planReference.LegalEntity);
        DimensionEntryControl.parmCompany(company.DataArea);
        DimensionEntryControl.reactivate();
    }
    return ret;
}

public void initValue()
{
    super();
    planReference.PlanType    = PlanTypes::ProductionFlow;
    planReference.LegalEntity = CompanyInfo::current();
}

public void write()
{
    LeanProductionFlowReference     leanProductionFlowReferenceLocal;
    ProdGroupId                     prodGroupId       = leanProductionFlowReference.ProdGroupId;
    boolean                         updateProdGroupId = !planReference.RecId;

    ttsbegin;
    super();

    if (updateProdGroupId)
    {
        leanProductionFlowReferenceLocal                     = LeanProductionFlowReference::findPlanReference(planReference.RecId,true);
        leanProductionFlowReferenceLocal.ProdGroupId         = prodGroupId;
        leanProductionFlowReferenceLocal.ProdGroupDataAreaId = appl.company().dataArea(tableNum(ProdGroup));
        leanProductionFlowReferenceLocal.update();

        leanProductionFlowReference.data(leanProductionFlowReferenceLocal);
    }
    ttscommit;
}

public void jumpRef()
{
    OMOperatingUnit     omOperatingUnitLocal;
    Args                args = new Args();

    omOperatingUnitLocal = OMOperatingUnit::find(planReference.ControllingOrganization,OMOperatingUnitType::OMValueStream);
    args.record(omOperatingUnitLocal);
    args.caller(this);

    new MenuFunction(menuitemDisplayStr(OMValueStream), MenuItemType::Display).run(args);
}

public void write()
{
    // insert is handled by planReference
    if (leanProductionFlowReference.RecId)
    {
        super();
    }
}

public int active()
{
    int ret = super();

    if (ret)
    {
        element.setFormControls();
    }

    return ret;
}
