[Form]
public class LeanProductionFlowVisualization extends FormRun
{
    private RecId               selectedPlanActivityRecId;
    private RecId               selectedPlanActivityRelationshipRecId;

    private QueryBuildRange     qbdrLastPlanPlanActivity_PlanActivityRecId;
    private QueryBuildRange     qbdrLeanProdFlowActivityPickingLocation_PlanActivityRecId;
    private QueryBuildRange     qbdrPlanActivity_PlanActivityRecId;

    private LeanProductionFlowVisualizationModel    leanProductionFlowVisualizationModel;
    private LeanProductionFlowVisualizationViewMode leanProductionFlowVisualizationViewMode;

    private LeanProductionFlowVisualizationControl  ctrlLeanProductionFlowVisualization;

}

public void init()
{
    leanProductionFlowVisualizationViewMode = LeanProductionFlowVisualizationViewMode::Location;

    if (element.args().dataset() != tableNum(Plan))
    {
        throw error(Error::wrongUseOfFunction(funcname()));
    }

    super();

    element.initializeVisualization();
    element.setViewModeButtonLabel(leanProductionFlowVisualizationViewMode);
}

private void initializeVisualization()
{
    ctrlLeanProductionFlowVisualization = grpVisualization.addControlEx(classStr(LeanProductionFlowVisualizationControl), 'ctrlLeanProductionFlowVisualization');
    ctrlLeanProductionFlowVisualization.widthMode(FormWidth::SizeToAvailable);
    ctrlLeanProductionFlowVisualization.heightMode(FormWidth::SizeToAvailable);

    ctrlLeanProductionFlowVisualization.onActivitySelected += eventhandler(element.onActivitySelected);
    ctrlLeanProductionFlowVisualization.onRelationSelected += eventhandler(element.onRelationSelected);
    ctrlLeanProductionFlowVisualization.onActivityDoubleClick += eventhandler(element.onActivityDoubleClick);
    ctrlLeanProductionFlowVisualization.onRelationDoubleClick += eventhandler(element.onRelationDoubleClick);
}

private void onActivitySelected(str _activityId)
{
    selectedPlanActivityRecId = _activityId ? any2Int64(_activityId) : 0;
    selectedPlanActivityRelationshipRecId = 0;

    element.refreshGrids();
    element.enableButtons();
}

private void onRelationSelected(str _relationId)
{
    selectedPlanActivityRecId = 0;
    selectedPlanActivityRelationshipRecId = _relationId ? any2Int64(_relationId) : 0;

    element.refreshGrids();
    element.enableButtons();
}

private void openDisplayMenuItem(str _menuItemStr, Common _record)
{
    Args args = new Args();
    args.record(_record);
    args.caller(element);

    new MenuFunction(_menuItemStr, MenuItemType::Display).run(args);
}

private void editActivity(RecId _planActivityId)
{
    this.openDisplayMenuItem(
        menuItemDisplayStr(PlanActivityDetails),
        PlanActivity::find(_planActivityId));
}

private void editRelation(RecId _planActivityRelationshipId)
{
    this.openDisplayMenuItem(
        menuItemDisplayStr(PlanActivityRelationEditPredecessor),
        LeanProdFlowPlanActivityRelation::findPlanActivityRelationship(_planActivityRelationshipId, plan.RecId, true));
}

private boolean isCurrentLegalEntity()
{
    return planReference.LegalEntity == CompanyInfo::current();
}

private void enableButtons()
{
    boolean isCurrentLegalEntity = this.isCurrentLegalEntity();
    boolean enableChange = (selectedPlanActivityRecId != 0 || selectedPlanActivityRelationshipRecId != 0) && isCurrentLegalEntity;

    btnEdit.enabled(enableChange);
    btnDelete.enabled(enableChange);

    btnGrpProductionFlow.enabled(isCurrentLegalEntity);
    btnKanbanBoard.enabled(PlanActivity.PlanActivityType == PlanActivityType::Process);

    if (!isCurrentLegalEntity)
    {
        btnGrpActionPane.enabled(false);
    }
}

public void init()
{
    super();

    QueryBuildDataSource qbds = this.queryBuildDataSource();

    qbds.clearLinks();

    qbds.addLink(fieldNum(KanbanFlow, LastPlanPlanActivity), fieldNum(PlanPlanActivity, RecId), KanbanFlow_DS.name());
    qbds.addRange(fieldNum(PlanPlanActivity, Plan)).value(queryValue(element.args().record().RecId));

    qbdrLastPlanPlanActivity_PlanActivityRecId = qbds.addRange(fieldNum(PlanPlanActivity, PlanActivity));
}

public int active()
{
    int ret = super();

    element.loadVisualizationData();
    element.enableButtons();

    return ret;
}

public void init()
{
    super();

    qbdrPlanActivity_PlanActivityRecId = this.queryBuildDataSource().addRange(fieldNum(PlanActivity, RecId));
}

public void init()
{
    super();

    QueryBuildDataSource qbds = this.query().dataSourceName(this.name());

    // filter kanban job statuses
    qbds.addRange(fieldNum(KanbanJob, Status)).value(SysQuery::valueNot(LeanKanbanJobStatus::Completed));

    // sort by scheduled date
    qbds.addSortField(fieldNum(KanbanJob, DueDateTime));
}

public void init()
{
    QueryBuildDataSource        qbds;
    QueryBuildDataSource        qbdsLeanProductionFlowActivity;
    QueryBuildDataSource        qbdsPlanActivityRelationship;
    QueryBuildDataSource        qbdsPlanActivityConstraint;
    QueryBuildDataSource        qbdsIndependentPlanActivity;

    super();

    // the picking activity locations are linked to the pick activities and not to the
    // high level activity. therefore we need to establish all necessary joins
    qbds = this.query().dataSourceName(this.name());
    qbds.clearLinks();
    qbds.clearDynalinks();

    // the dependent lean production flow activity (pick)
    qbdsLeanProductionFlowActivity = qbds.addDataSource(tableNum(LeanProductionFlowActivity));
    qbdsLeanProductionFlowActivity.fetchMode(QueryFetchMode::One2One);
    qbdsLeanProductionFlowActivity.joinMode(JoinMode::ExistsJoin);
    qbdsLeanProductionFlowActivity.addLink(fieldNum(LeanProdFlowActivityPickingLocation, ProductionFlowActivity),
                                           fieldNum(LeanProductionFlowActivity, RecId));

    // find independent top level plan activity
    qbdsPlanActivityRelationship = qbdsLeanProductionFlowActivity.addDataSource(tableNum(PlanActivityRelationship));
    qbdsPlanActivityRelationship.fetchMode(QueryFetchMode::One2One);
    qbdsPlanActivityRelationship.joinMode(JoinMode::ExistsJoin);
    qbdsPlanActivityRelationship.addLink(fieldNum(LeanProductionFlowActivity, PlanActivity),
                                         fieldNum(PlanActivityRelationship, DependentPlanActivity));

    // aggregation constraint
    qbdsPlanActivityConstraint = qbdsPlanActivityRelationship.addDataSource(tableNum(PlanConstraint));
    qbdsPlanActivityConstraint.fetchMode(QueryFetchMode::One2One);
    qbdsPlanActivityConstraint.joinMode(JoinMode::ExistsJoin);
    qbdsPlanActivityConstraint.addLink(fieldNum(PlanActivityRelationship, Constraint),
                                       fieldNum(PlanConstraint, RecId));
    qbdsPlanActivityConstraint.addRange(fieldNum(PlanConstraint, ConstraintType)).value(queryValue(ConstraintTypes::Aggregation));

    qbdsIndependentPlanActivity = qbdsPlanActivityRelationship.addDataSource(tableNum(PlanActivity));
    qbdsIndependentPlanActivity.fetchMode(QueryFetchMode::One2One);
    qbdsIndependentPlanActivity.joinMode(JoinMode::ExistsJoin);
    qbdsIndependentPlanActivity.addLink(fieldNum(PlanActivityRelationship, IndependentPlanActivity),
                                        fieldNum(PlanActivity, RecId));

    // range on independent activity
    qbdrLeanProdFlowActivityPickingLocation_PlanActivityRecId = qbdsIndependentPlanActivity.addRange(fieldNum(PlanActivity, RecId));
}

public void clicked()
{
    super();

    if (selectedPlanActivityRecId != 0)
    {
        element.editActivity(selectedPlanActivityRecId);
        element.loadVisualizationData();
    }

    if (selectedPlanActivityRelationshipRecId != 0)
    {
        element.editRelation(selectedPlanActivityRelationshipRecId);
    }
}

public void clicked()
{
    super();

    if (selectedPlanActivityRecId != 0)
    {
        planPlanActivity.delete();
    }

    if (selectedPlanActivityRelationshipRecId != 0)
    {
        PlanActivityRelationship::find(selectedPlanActivityRelationshipRecId, true).delete();
    }

    element.loadVisualizationData();
}

public void jumpRef()
{
    super();

    KanbanRule::jumpRefKanbanRule(kanbanRule, this);
}
