[Form]
public class LeanRuleReassignmentWizard extends FormRun
{
    LeanRuleReassignmentWizard  sysWizard;
}

public void init()
{
    if (element.args().caller())
    {
        sysWizard = element.args().caller();
    }
    else
    {
        throw error(Error::wrongUseOfFunction(funcName()));
    }

    super();

    sysWizard.parmInventDim(inventDim_ds);
    sysWizard.updateDesign(InventDimFormDesignUpdate::Init);
    sysWizard.updateDesign(InventDimFormDesignUpdate::Active);

    leanReplenishmentStrategy.text(enum2str(sysWizard.currentKanbanRule().ReplenishmentStrategy));

    this.setButtonAccess();
}

public void setButtonAccess()
{
    int     totalKanbans    = sysWizard.parmKanbans().elements();
    int     selectedKanbans = kanban_ds.recordsMarked().lastIndex();

    // Ensure we cannot delete all the kanbans
    remove.enabled(   totalKanbans > 1
                   && totalKanbans != selectedKanbans);
}

public void executeQuery()
{
    QueryBuildRange     queryBuildRange;
    SetEnumerator       se;
    RefRecId            kanbanRecId;

    // Filter by the list of kanbans specified
    this.query().dataSourceTable(tableNum(Kanban)).clearRanges();
    se = sysWizard.parmKanbans().getEnumerator();
    while (se.moveNext())
    {
        kanbanRecId = se.current();
        queryBuildRange = this.query().dataSourceTable(tableNum(Kanban)).addRange(fieldNum(Kanban, RecId));
        queryBuildRange.value(queryValue(kanbanRecId));
    }

    super();
}

public void init()
{
    Query                   query;
    QueryBuildDataSource    qbds;

    super();

    query = kanbanFlowTmpActivityRelations_ds.query();
    qbds = query.dataSourceTable(tableNum(KanbanFlowTmpActivityRelations));
    qbds.addOrderByField(fieldNum(KanbanFlowTmpActivityRelations, RecId), SortOrder::Ascending);
}

public void clicked()
{
    Kanban          kanbanSelected;
    Set             kanbans = sysWizard.parmKanbans();

    super();

    // Get the first multi selection or single selection record
    kanbanSelected = kanban_ds.getFirst(1) ?
                     kanban_ds.getFirst(1) :
                     kanban_ds.cursor();
    // Delete all the records selected
    while (kanbanSelected)
    {
        kanbans.remove(kanbanSelected.RecId);
        kanbanSelected = kanban_ds.getNext();
    }

    element.setButtonAccess();

    kanban_ds.executeQuery();
}

public void jumpRef()
{
    super();

    Kanban::jumpRefKanban(kanban, this);
}

public void lookup()
{
    KanbanRule::lookupReassignRule(this, sysWizard.currentKanbanRule().ruleData(), true);
}

public boolean modified()
{
    boolean     ret;
    KanbanRule  kanbanRule;
    str         previousKanbanRule = leanReplenishmentRuleId.valueStr();

    ret = super();

    if (ret)
    {
        kanbanRule = KanbanRule::findRuleId(leanReplenishmentRuleId.valueStr());

        ret = sysWizard.updateKanbanRule(kanbanRule);
        if (ret)
        {
            kanbanFlowTmpActivityRelations.init(kanbanRule,true);
            kanbanFlowTmpActivityRelations_ds.executeQuery();
        }
        else
        {
            leanReplenishmentRuleId.text(previousKanbanRule);
        }
    }

    return ret;
}

public boolean validate()
{
    boolean ret;

    ret = super();

    ret = ret && KanbanRule::findRuleId(this.valueStr(),false).RecId;

    return ret;
}
