private void addDimensionsLinks()
{
    Query                   query = generalJournalEntry_ds.query();
    QueryBuildDataSource    ds;
    RecId                   mainAccountRecId = DimensionAttribute::getWellKnownDimensionAttribute(DimensionAttributeType::MainAccount);
    QueryBuildRange         range;

    // manually add the relationship to the view
    ds = query.dataSourceTable(tableNum(DimensionAttributeLevelValueView));
    ds.addLink(fieldNum(GeneralJournalAccountEntry, LedgerDimension), fieldNum(DimensionAttributeLevelValueView, ValueCombinationRecId));

    // manually add the relationship from the view
    ds = query.dataSourceTable(tableNum(MainAccount));
    ds.addLink(fieldNum(DimensionAttributeLevelValueView, EntityInstance), fieldNum(MainAccount, RecId));

    ds = query.dataSourceTable(tableNum(DimensionAttributeLevelValueView));

    // manually add the relationship to the main account
    range = SysQuery::findOrCreateRange(ds, fieldNum(DimensionAttributeLevelValueView, DimensionAttribute));
    range.value(SysQuery::value(mainAccountRecId));
}

void bondItem2Item(FormListItem _sourceItem, FormListItem _targetItem)
{
    LedgerBondTransObject_RU bondTransObjectSource,bondTransObjectTarget;
    ;
    bondTransObjectSource = bond.server().findBondTransObject(_sourceItem.data());
    bondTransObjectTarget = bond.server().findBondTransObject(_targetItem.data());
    if (bondTransObjectSource.remainAmountCur() == 0)
    {
        this.processDiff(bondTransObjectSource,bondTransObjectTarget);
    }
    else if (bondTransObjectTarget.remainAmountCur() == 0)
    {
        this.processDiff(bondTransObjectTarget,bondTransObjectSource);
    }
    else
    {
        bond.client().bondVRef2VRef(_sourceItem.data(), _targetItem.data());
    }
    if (bond.server().fatalErrorState())
    {
        this.reloadBondServer();
    }
    this.refreshDesign();
}

void bondList2List(FormListNext _formListNext)
{
    container creditLog, debitLog;

    container list2log(FormListControl _list)
    {
        int             iIndx;
        FormListItem    formListItem;
        container       ret;

        iIndx = _list.getNextItem(_formListNext);
        while (iIndx != -1)
        {
            formListItem = _list.getItem(iIndx);
            ret += formListItem.data();
            iIndx = _list.getNextItem(_formListNext, iIndx);
        }
        return ret;
    }
    ;

    debitLog = list2log(listDebitTransaction);
    creditLog = list2log(listCreditTransaction);

    if (conLen(debitLog)==1 && conLen(creditLog)==1)
    {
        this.bondItem2Item(listDebitTransaction.getItem(listDebitTransaction.getNextItem(_formListNext)),
                           listCreditTransaction.getItem(listCreditTransaction.getNextItem(_formListNext)));
    }
    else if (conLen(debitLog) && conLen(creditLog))
    {
        bond.client().bondLog2Log(transDate, debitLog, creditLog);
        if (bond.server().fatalErrorState())
        {
            this.reloadBondServer();
        }
        this.refreshDesign();
    }
}

public boolean canClose()
{
    boolean ret;

    ret = super();

    if (application.ttsLevel())
    {
        if (Box::yesNo(strFmt("@GLS102511", vouchers.Voucher), DialogButton::Yes, "@GLS102512", "@GLS102513") == DialogButton::Yes)
        {
            if (! element.saveTransactions())
            {
                ttsabort;
            }
        }
        else
        {
            ttsabort;
        }
    }

    return ret;
}

public void init()
{
    #ResAppl
    #define.FirstAmountFieldIndex(3)

    Args        args        = element.args();
    int         amountLen   = SysDictType::newName(extendedTypeStr(AmountCur)).displayLength();
    container   columns     = ["@SYS15996", "@SYS7572", "@SYS23187", "@SYS4478", "@SYS53088"];

    void initColumns(FormListControl _list)
    {
        int i;
        FormListColumn column;
        for (i=1; i<=conLen(columns); i++)
        {
            column = new FormListColumn(conPeek(columns, i), i);
            if (i > #FirstAmountFieldIndex)
            {
                column.format(FormListFormat::Right);
                column.text(conPeek(columns, i));
            }
            _list.addColumn(i, column);
        }
    }
    if (args.caller())
    {
        parmVoucher = args.caller().parmVoucher();
    }

    super();

    application = new Application();

    initColumns(listDebitTransaction);
    initColumns(listCreditTransaction);

    if (voucher)
    {
        showVouchers.visible(false);
    }
    this.addDimensionsLinks();
}

private void initTransactionList(FormListControl _list, NoYes _crediting)
{
    LedgerBondTransList_RU      bondTransList = bond.server().transList();
    LedgerBondTransObject_RU    bondTransObject;
    boolean                     more;
    void addItems(container _captions)
    {
        FormListItem item;
        int i;
        for (i=1; i<=conLen(_captions); i++)
        {
            item = new FormListItem(conPeek(_captions, i));
            item.subItem(i);
            _list.setItem(item);
        }
    }

    _list.lockWindowUpdate(true);
    _list.deleteAll();

    bondTransList.parmTransDate(dateNull());
    for (more = bondTransList.first();
    more;
    more = bondTransList.next())
    {
        bondTransObject  = bondTransList.item();
        if (bondTransObject.remainCrediting() == _crediting &&
            ! bondTransObject.bondedCompletelyAll())
        {
            _list.addItem(
                new FormListItem(bondTransObject.ledgerTransObject().dimensionDisplayValue(), 0,
                                 bondTransObject.vrefId())
            );

            addItems([bondTransObject.ledgerTransObject().parmTransactionCurrencyCode(),
                      strFmt("%1", bondTransObject.remainAmountCur()),
                      strFmt("%1", bondTransObject.remainAmountMST()),
                      strFmt("%1", bondTransObject.remainAmountMSTSecond())]);
        }
    }

    _list.lockWindowUpdate(false);
}

void processDiff(LedgerBondTransObject_RU _bondTransDiff,
                 LedgerBondTransObject_RU _bondTransObject)
{
    Amount amount;
    if (_bondTransDiff.remainAmountMST())
    {
        amount = min(_bondTransDiff.remainAmountMST(),_bondTransObject.remainAmountMST());
        if (_bondTransDiff.remainCrediting())
        {
            bond.server().addBondMSTDiff(_bondTransObject.vrefId(),
                                            _bondTransDiff.vrefId(),
                                            amount);
        }
        else
        {
            bond.server().addBondMSTDiff(_bondTransDiff.vrefId(),
                                            _bondTransObject.vrefId(),
                                            amount);
        }
    }
    else
    {
        amount = min(_bondTransDiff.remainAmountMSTSecond(),_bondTransObject.remainAmountMSTSecond());
        if (_bondTransDiff.remainCrediting())
        {
            bond.server().addBondMSTSecondDiff(_bondTransObject.vrefId(),
                                                  _bondTransDiff.vrefId(),
                                                  amount);
        }
        else
        {
            bond.server().addBondMSTSecondDiff(_bondTransDiff.vrefId(),
                                                  _bondTransObject.vrefId(),
                                                  amount);
        }
    }
}

void refreshDesign(boolean _callActive = true)
{
    if (_callActive)
    {
        vouchers_ds.active(false);
    }

    this.initTransactionList(listDebitTransaction, NoYes::No);
    this.initTransactionList(listCreditTransaction, NoYes::Yes);

    tmpBondWorkTable.setTmpData(bond.server().tmpBond());
    tmpBondWorkTable_ds.research();

    buttonSave.enabled(bond.server().tmpBond().RecId);
}

void reloadBondServer()
{
    bond    = LedgerBondManual_RU::construct(vouchers);
}

void researchVouchers()
{
    GeneralJournalEntry reference;
    boolean     found = true;

    if (showVouchers.visible())
    {
        switch (showVouchers.selection())
        {
            case LedgerBondShowMode_RU::NoBond:
                while (found            &&
                       vouchers.RecId   &&
                       GeneralJournalAccountEntry_W::entryIsBondedCompletely_RU(vouchers_GeneralJournalEntry.RecId))
                {
                    found = vouchers_ds.next();
                }
                break;

            case LedgerBondShowMode_RU::Bonded:
                while (found            &&
                       vouchers.RecId   &&
                       !GeneralJournalAccountEntry_W::entryIsBondedCompletely_RU(vouchers_GeneralJournalEntry.RecId))
                {
                    found = vouchers_ds.next();
                }
                break;
        }

        reference.data(vouchers);

        vouchers_ds.executeQuery();
        vouchers_ds.positionToRecord(reference);
        vouchers_ds.active();
    }
    else
    {
        vouchers_ds.executeQuery();
    }
}

boolean saveTransactions()
{
    boolean ret;

    ret = bond.save(comboBoxDetailSummary.selection());

    if (ret)
    {
        while (application.ttsLevel())
        {
            ttscommit;
        }
    }
    else
    {
        if (! application.ttsLevel())
        {
            throw error("@SYS18447");
        }
    }

    this.reloadBondServer();
    this.refreshDesign();
    return ret;
}

public int task(int _p1)
{
    int ret;
    if (! application.ttsLevel())
        ret = super(_p1);

    return ret;
}

public int active(boolean _refreshBondPage = true)
{
    int ret = super();

    if (ret)
    {
        buttonRemoveBond.enabled(GeneralJournalAccountEntry_W::entryhasBondedTransactions_RU(vouchers_GeneralJournalEntry.RecId));

        if (_refreshBondPage && TabPageBond.isExpanded())
        {
            element.reloadBondServer();
            element.refreshDesign(false);
        }
    }

    return ret;
}

public void displayOption(Common _record, FormRowDisplayOption _options)
{
    #Define.gray(196)
    SubledgerVoucherGeneralJournalEntry subledgerVoucherGeneralJournalEntry = _record;

    if (showVouchers.selection() == LedgerBondShowMode_RU::All && !GeneralJournalAccountEntry_W::entryIsBondedCompletely_RU(subledgerVoucherGeneralJournalEntry.GeneralJournalEntry))
    {
        _options.backColor(WinAPI::RGB2int(#gray, #gray, #gray));
    }

