}

public void executeQuery()
{
    Query                   query = this.query();
    QueryBuildDataSource    vouchersDS;
    QueryBuildDataSource    journalEntryDS;
    QueryBuildDataSource    ds;

    vouchersDS = query.dataSourceTable(tableNum(SubledgerVoucherGeneralJournalEntry));
    if (parmVoucher)
    {
        findOrCreateRange_W(vouchersDS, fieldNum(SubledgerVoucherGeneralJournalEntry, Voucher), parmVoucher);
    }

    journalEntryDS = query.dataSourceTable(tableNum(GeneralJournalEntry));

    findOrCreateRange_W(journalEntryDS, fieldNum(GeneralJournalEntry, Ledger), queryValue(Ledger::current()))
        .status(RangeStatus::Hidden);

    ds = query.dataSourceTable(tableNum(GeneralJournalAccountEntry_W));

    findOrCreateRange_W(
        ds, 
        fieldNum(GeneralJournalAccountEntry_W, GeneralJournalEntry),
        strFmt('(%1.%2 = %3.%4)',
        ds.name(),
        fieldStr(GeneralJournalAccountEntry_W, GeneralJournalEntry),
        journalEntryDS.name(),
        fieldStr(GeneralJournalEntry, RecId)));

    switch (showVouchers.selection())
    {
        case LedgerBondShowMode_RU::All:
            findOrCreateRange_W(ds, fieldNum(GeneralJournalAccountEntry_W, BondBatchTrans_RU), SysQuery::valueUnlimited());
            ds.joinMode(JoinMode::ExistsJoin);
            break;

        case LedgerBondShowMode_RU::NoBond:
            findOrCreateRange_W(ds, fieldNum(GeneralJournalAccountEntry_W, BondBatchTrans_RU), queryValue(0));
            ds.joinMode(JoinMode::ExistsJoin);
            break;

        case LedgerBondShowMode_RU::Bonded:
            findOrCreateRange_W(ds, fieldNum(GeneralJournalAccountEntry_W, BondBatchTrans_RU), queryValue(0));
            ds.joinMode(JoinMode::NoExistsJoin);
            break;
    }

    super();
}

