/// <summary>
/// Initializes the consolidation class variables that needed to process to the values from the form upon closing the form.
/// </summary>
public void close()
{
    TmpLedgerConsCompany    tmpCompany;
    TmpLedgerConsElimination    tmpElimination;
    container               c;
    container               eliminationTmp;
    boolean                 closeCalled;

    if (consolidate.checkCloseDialog())
    {
        if (!closeCalled)
        {
            tmpCompany.setTmpData(companies);
            while select tmpCompany
            {
                c = conIns(c, 999, [tmpCompany.SourceCompany, tmpCompany.Proportion, tmpCompany.ProfitLossBalance, tmpCompany.FileName]);
            }

            consolidate.companies(c);

            // load the contents of the tmp table into the eliminations container on the consolidation class
            tmpElimination.setTmpData(eliminations);
            while select tmpElimination
            {
                eliminationTmp = conIns(eliminationTmp,999,[tmpElimination.RuleId,tmpElimination.ReasonRefRecId]); //5637144577]);
            }

            consolidate.parmEliminations(eliminationTmp);

            closeCalled = true;
        }
    }

    super();
}

