/// <summary>
/// Enables or disables and defaults selection and values in the consolidation account related fields.
/// </summary>
public void enableOrDisableAccountGroupFields()
{
    if (consolidate.companyGroupAccounts())
    {
        ConsolidationAccountSource.enabled(true);

        // Enable the group only if the selection is set to "from consolidation account group"
        if (ConsolidationAccountSource.selection() == ConsolidationAccountSource_FromAccountGroup)
        {
            ConsolidateAccountGroup.enabled(true);
        }
        else
        {
            // Clear the reference if selecting "from main account" or it's blank.
            ConsolidateAccountGroup.enabled(false);
            ConsolidateAccountGroup.value(0);

            // Also clear the account group to keep in sync with the checkbox
            consolidate.parmConsolidateAccountGroup(0);
        }
    }
    else
    {
        ConsolidationAccountSource.enabled(false);
        ConsolidateAccountGroup.enabled(false);

        // Also clear the account group to keep in sync with the checkbox
        consolidate.parmConsolidateAccountGroup(0);
    }
}

/// <summary>
/// Creates a <c>LedgerConsolidateSourceDimension</c> record for each <c>DimensionAttribute</c> in the given company.
/// </summary>
public void fillSourceAttributes()
{
    LedgerConsolidateSourceDimension    ledgerConsolidateSourceDimension;
    LedgerConsolidateSourceDimension    ledgerConsolidateSourceDimension2;
    DimensionAttribute                  dimensionAttribute;

    while select Name, RecId from dimensionAttribute order by Name asc
            where dimensionAttribute.Type == DimensionAttributeType::CustomList ||
                dimensionAttribute.Type == DimensionAttributeType::ExistingList
    {
        select RecId from ledgerConsolidateSourceDimension2
            where ledgerConsolidateSourceDimension2.DimensionAttribute == dimensionAttribute.RecId;
        if (ledgerConsolidateSourceDimension2.RecId == 0)
        {
            ledgerConsolidateSourceDimension.DimensionAttribute = dimensionAttribute.RecId;
            ledgerConsolidateSourceDimension.Specification = LedgerConsDim::None;
            ledgerConsolidateSourceDimension.Ordinal = 0;
            ledgerConsolidateSourceDimension.insert();
        }
    }

    // We need to remove any records populated from SysLastValue for DimensionAttributes that no longer exist.
    delete_from ledgerConsolidateSourceDimension notexists join dimensionAttribute
        where dimensionAttribute.RecId == ledgerConsolidateSourceDimension.DimensionAttribute;
}

