/// <summary>
/// Initializes the form values and display options when the form opens.
/// </summary>
/// <exception cref="Exception::Error">
/// The form is not called by a consolidation class.
/// </exception>
public void init()
{
    DialogRunbase caller = element.args().caller();
    consolidate = caller.caller();

    if (!consolidate)
    {
        throw error(Error::missingParameter(this));
    }

    super();

    element.design().caption(strFmt('%1 [%2]', element.design().caption(), consolidate.mode()));

    switch (consolidate.mode())
    {
        case LedgerConsMode::Online:
            element.design().dialogSize(SysDialogSize::Full);
            consolidate.parmGlPostingDate(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()));
            break;

        case LedgerConsMode::export:
            companies_Proportion.visible(false);
            description.visible(false);
            elimination.visible(false);
            currencyTranslation.visible(false);
            consBudgetExchangeRateType.visible(false);
            processFullBalanceRebuild.visible(false);
            companies_FileName.visible(true);
            break;

        case LedgerConsMode::Import:
            elimination.visible(false);
            financialDimension.visible(false);
            companies_UserProvidedFileName.visible(true);

            // <GBR>
            if (BrazilParameters::isEnabled())
            {
                TransferTransactionsOnly_BR.visible(false);
            }
            // </GBR>
            break;
    }

    // Default the consolidation account related fields
    switch (consolidate.mode())
    {
        case LedgerConsMode::Online:
        case LedgerConsMode::Export:
            companyGroupAccounts1.visible(true);
            consolidationAccountSource.visible(true);
            consolidateAccountGroup.visible(true);

            // Properly populate the controls from the SysLastValues loaded settings already unpacked in the LedgerConsolidate class
            if (consolidate.companyGroupAccounts())
            {
                if (consolidate.parmConsolidateAccountGroup())
                {
                    ConsolidationAccountSource.selection(ConsolidationAccountSource_FromAccountGroup);
                    ConsolidateAccountGroup.value(consolidate.parmConsolidateAccountGroup());
                }
                else
                {
                    ConsolidationAccountSource.selection(ConsolidationAccountSource_FromMainAccount);
                }
            }
            else
            {
                ConsolidationAccountSource.selection(ConsolidationAccountSource_NoSelection);
            }

            element.enableOrDisableAccountGroupFields();
            break;
    }

    // Assign field values on Criteria tab selected on previous execution.
    consBudgetExchangeRateType.value(consolidate.parmBudgetExchangeRateType());
    fromAccount.text(consolidate.fromAccount());
    toAccount.text(consolidate.toAccount());

    consolidationAmountSource.selection(consolidate.consolidationAmountSource());
    this.setUseDualCurrency();

    // Load the data for the financial dimensions tab.
    element.fillSourceAttributes();

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        element.applyTransferTransOnlyRules_BR();
    }
    // </GBR>

    LedgerConsolidate::updateCurrencyConversionDefaultExchRate(element.getConsolidationTransDate());
    ledgerConsolidateCurrencyConversion_ds.research(true);
}

